DROP TABLE IF EXISTS Courses;

CREATE TABLE `Courses`
(
    `Organization`	VARCHAR(512),
    `CourseSubjectAbbreviation`	VARCHAR(512) NOT NULL,
    `CourseNumber`	VARCHAR(256) NOT NULL,
    `CourseShortTitle`	VARCHAR(512) NOT NULL ,
    `CourseLongTitle`	VARCHAR(512) NOT NULL ,
    `CourseDescription`	TEXT NOT NULL ,
    `CourseEffectiveDate`	INT,
    `CourseCreditBasis`	VARCHAR(512),
    `CourseCreditUnits`	VARCHAR(512),
    `CourseCreditMinimumValue`	VARCHAR(512),
    `CourseCreditMaximumValue`	VARCHAR(512),
    `CourseLevel`	VARCHAR(512),
    `CourseAttribute`	VARCHAR(512),
    PRIMARY KEY (`CourseSubjectAbbreviation`,`CourseNumber`)
);

