INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseCreditMaximumValue`, `CourseLevel`, `CourseAttribute`) VALUES ('', 'ECE', '15200', 'Programming For Engineers', 'Programming For Engineers', 'Credit Hours: 3.00.  Introductory C programming course. Students will be introduced to basic syntax, standard mathematics library, control structures, user-defined functions, arrays, pointers, structures, and file I/Os. Laboratory exercises will accelerate learning of fundamental materials through supervised practice.', '202108', 'Regular', '', '0', '3', 'GRGraduate', 'LOWRLower Division');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseCreditMaximumValue`, `CourseLevel`, `CourseAttribute`) VALUES ('', 'ECE', '19000', 'Introduction To ECE', 'Introduction To ECE', 'Credit Hours: 1.00.  This course is intended to provide an introduction to electrical and computer engineering for students in their freshman year.  A goal is to provide some historical background of the respective sub-areas within ECE, a description of analytical tools that will be developed throughout their curriculum, the motivation for the tools, and to inform students of elective courses in ECE.', '202108', 'Regular', '', '1', '', 'GRGraduate', 'LOWRLower Division');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseCreditMaximumValue`, `CourseLevel`, `CourseAttribute`) VALUES ('', 'ECE', '19595', 'Selected Topics In ECE', 'Selected Topics In ECE', 'Credit Hours: 1.00 to 5.00.  Topics vary. Permission of department required.', '202108', 'Regular', '', '1', '5', 'GRGraduate', 'VARTVariable Title');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseCreditMaximumValue`, `CourseLevel`, `CourseAttribute`) VALUES ('', 'ECE', '20000', 'Elec & Comptr Engr Sem', 'Elec & Comptr Engr Sem', 'Credit Hours: 0.00.  An introduction to the School of Electrical and Computer Engineering, ECE program objectives and outcomes, BSEE & BSCmpE degree requirements, and professional development.', '202108', 'Regular', '', '0', '', 'GRGraduate', 'LOWRLower Division');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseCreditMaximumValue`, `CourseLevel`, `CourseAttribute`) VALUES ('', 'ECE', '20001', 'Electrical Engr Fundamentals I', 'Electrical Engr Fundamentals I', 'Credit Hours: 3.00.  This course covers fundamental concepts and applications for electrical and computer engineers as well as for engineers who need to gain a broad understanding of these disciplines. The course starts by the basic concepts of charge, current, and voltage as well as their expressions with regards to resistors and resistive circuits. Essential concepts, devices, theorems, and applications of direct-current (DC), 1st order, and alternating-current (AC) circuits are subsequently discussed. Besides electrical devices and circuits, basic electronic components including diodes and transistors as well as their primary applications are also discussed.', '202201', 'Regular', '', '0', '3', 'PRProfessional', 'LOWRLower Division');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseCreditMaximumValue`, `CourseLevel`, `CourseAttribute`) VALUES ('', 'ECE', '20002', 'Elect Engr Fundamental II', 'Elect Engr Fundamental II', 'Credit Hours: 3.00.  Continuation of Electrical and Computer Engineering Fundamentals I. The course addresses mathematical and computational foundations of circuit analysis (differential equations, Laplace Transform techniques) with a focus on application to linear circuits having variable behavior as a function of frequency, with emphasis on filtering. Variable frequency behavior is further considered for applications of electronic components through single-transistor and operational amplifiers. The course ends with consideration of how circuits behave and may be modeled for analysis at high frequencies. Permission of Department required.', '202108', 'Regular', '', '3', '', 'PRProfessional', 'LOWRLower Division');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseCreditMaximumValue`, `CourseLevel`, `CourseAttribute`) VALUES ('', 'ECE', '20007', 'Elec Engr Fundamentals I Lab', 'Elec Engr Fundamentals I Lab', 'Credit Hours: 1.00.  This is an introduction course in electronic measurement and circuit modeling, simulation and design techniques. These skills are developed through a variety of laboratory experiments ranging from voltage, current, and frequency, to resistors, inductors, capacitors, and operational amplifiers. When possible, the experiments develop practical skills through small design and soldering tasks. Finally, the course culminates in a two week group design project.  In many ways this course is the laboratory of the co-requisite lecture course ECE 20001: Electrical Engineering Fundamentals I; however, we remind students that this is a standalone course that expects students will learn and demonstrate material not taught in ECE 20001.', '202108', 'Regular', '', '1', '', 'PRProfessional', 'LOWRLower Division');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseCreditMaximumValue`, `CourseLevel`, `CourseAttribute`) VALUES ('', 'ECE', '20008', 'Elec Engr Fundamentals II Lab', 'Elec Engr Fundamentals II Lab', 'Credit Hours: 1.00.  This is a course in electronic measurement, circuit modeling, simulation and design techniques. These skills are developed through a variety of laboratory experiments including discrete semiconductor measurement, transistor amplifiers, motor control, and operational amplifier internals. The experiments develop practical skills through small design and soldering tasks. Finally, the course culminates in a two week group design project. In many ways this course is the laboratory of the co-requisite lecture course ECE 20002 Electrical Engineering Fundamentals II; however, we remind students that this is a standalone course that expects students will learn and demonstrate material not taught in other courses.', '202108', 'Regular', '', '1', '', 'PRProfessional', 'LOWRLower Division');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseCreditMaximumValue`, `CourseLevel`, `CourseAttribute`) VALUES ('', 'ECE', '20100', 'Linear Circuit Anly I', 'Linear Circuit Anly I', 'Credit Hours: 3.00.  Volt-ampere characteristics for circuit elements; independent and dependent sources; Kirchhoff\'s laws and circuit equations. Source transformations; Thevenin\'s and Norton\'s theorems; superposition, step response of 1st order (RC, RL) and 2nd order (RLC) circuits. Phasor analysis, impedance calculations, and computation of sinusoidal steady state responses. Instantaneous and average power, complex power, power factor correction, and maximum power transfer. Instantaneous and average power.', '202205', 'Regular', '', '3', '', 'GRGraduate', 'LOWRLower Division');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseCreditMaximumValue`, `CourseLevel`, `CourseAttribute`) VALUES ('', 'ECE', '20200', 'Linear Circuit Anly II', 'Linear Circuit Anly II', 'Credit Hours: 3.00.  Continuation of ECE 20100. Use of Laplace Transform techniques to analyze linear circuits with and without initial conditions. Characterization of circuits based upon impedance, admittance, and transfer function parameters. Determination of frequency response via analysis of poles and zeros in the complex plane. Relationship between the transfer function and the impulse response of a circuit. Use of continuous time convolution to determine time domain responses. Properties and practical uses of resonant circuits and transformers. Input - output characterization of a circuit as a two-port. Low and high-pass filter design.', '202108', 'Regular', '', '3', '', 'GRGraduate', 'LOWRLower Division');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseCreditMaximumValue`, `CourseLevel`, `CourseAttribute`) VALUES ('', 'ECE', '20400', 'Int Elec & Electr Circ', 'Int Elec & Electr Circ', 'Credit Hours: 4.00.  Students will learn basics of electrical and electronic circuits including introduction to analog and digital electronic circuits.  Measurement of electrical signals using meters, probes, and oscilloscopes are covered in the laboratory component of the course. Circuits are designed for minimum hardware with emphasis on understanding analog and digital electronics with particular use of digital and analog microchips.  Non-ECE majors who complete this course can continue the digital course sequence offered by the ECE department including microprocessor systems and interfacing, and digital signal processing. No credit will be given for ECE majors.', '202108', 'Regular', '', '4', '', 'GRGraduate', 'LOWRLower Division');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseCreditMaximumValue`, `CourseLevel`, `CourseAttribute`) VALUES ('', 'ECE', '20500', 'Int Elec & Electr Circ', 'Int Elec & Electr Circ', 'Credit Hours: 3.00.  Students will learn basics of electrical and electronic circuits including introduction to analog and digital electronic circuits.  Circuits are designed for minimum hardware with emphasis on understanding analog and digital electronics with particular use of digital and analog microchips. Non-ECE majors who complete this course can continue the digital course sequence offered by the ECE department including microprocessor systems and interfacing, and digital signal processing. No credit will be given for ECE majors.', '202108', 'Regular', '', '3', '', 'GRGraduate', 'LOWRLower Division');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseCreditMaximumValue`, `CourseLevel`, `CourseAttribute`) VALUES ('', 'ECE', '20501', 'Intro Elec Circs, Sens, Motors', 'Intro Elec Circs, Sens, Motors', 'Credit Hours: 3.00.  The basics of electrical circuit analysis using Kirchhoff\'s laws, node voltage, mesh current, superposition, the maximum power theorem; transient analysis of RL / RC circuits; Bipolar Junction Transistor DC analysis; basics of simple sensors; basic operation of rotating electric machines; basics of digital logic circuits. Non-ECE majors only.', '202301', 'Regular', '', '0', '3', 'PRProfessional', 'LOWRLower Division');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseCreditMaximumValue`, `CourseLevel`, `CourseAttribute`) VALUES ('', 'ECE', '20502', 'Intro Elec Cir,Sen,& Motor Lab', 'Intro Elec Cir,Sen,& Motor Lab', 'Credit Hours: 1.00.  Students will learn the basics of electrical signal measurements using meters, probes, oscilloscopes; basic measurements using simple sensors; basic measurements as applied to motors. Circuits are designed for basic hardware with emphasis on understanding analog and digital electronics with practical use of digital and analog-interface microcontrollers. Non-ECE majors only.', '202301', 'Regular', '', '1', '', 'PRProfessional', 'LOWRLower Division');
