INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseCreditMaximumValue`, `CourseLevel`, `CourseAttribute`) VALUES ('', 'ECE', '22900', 'C Programming For ECE', 'C Programming For ECE', 'Credit Hours: 3.00. An introductory course on the programming in C, with emphasis on applications in electrical and computer engineering. Topics include files, structures, arrays, pointers, and the proper use of dynamic data structures. Students are expected to design and test software programs to solve engineering problems.', '202308', 'Regular', '', '0', '3', 'PRProfessional', 'LOWRLower Division');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseCreditMaximumValue`, `CourseLevel`, `CourseAttribute`) VALUES ('', 'ECE', '23000', 'Engr Data Analysis In Python', 'Engr Data Analysis In Python', 'Credit Hours: 3.00.  This course introduces data analysis to engineering students through Python programming. Students learn Python programming and introductory data science topics. The topics include data sampling and estimation, classification, clustering and advanced data analysis approaches. Students will be able to use Python as the programming language to solve data science problems in their course and research work.', '202308', 'Regular', '', '3', '', 'PRProfessional', 'LOWRLower Division');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseCreditMaximumValue`, `CourseLevel`, `CourseAttribute`) VALUES ('', 'ECE', '25100', 'Object Orient Prog', 'Object Orient Prog', 'Credit Hours: 3.00.  The C++ and Java programming languages are presented. Students will be introduced to classes, inheritance, polymorphism, class derivation, abstract classes, interfaces, function overloading and overriding, container classes and template classes.', '202108', 'Regular', '', '0', '3', 'GRGraduate', 'LOWRLower Division');
