INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseCreditMaximumValue`, `CourseLevel`, `CourseAttribute`) VALUES ('', 'ECE', '29600', 'Electrical & Comp Eng Projects', 'Electrical & Comp Eng Projects', 'Credit Hours: 0.00 to 18.00.  Projects in Electrical and Computer Engineering. Permission of Department required.', '202108', 'Regular', '', '0', '18', 'GRGraduate', 'VARTVariable Title');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseCreditMaximumValue`, `CourseLevel`, `CourseAttribute`) VALUES ('', 'ECE', '30001', 'Signals And Systems Lab', 'Signals And Systems Lab', 'Credit Hours: 1.00.  Experiments covering the analysis and design of continuous and discrete time signals and systems in both the time and frequency-domains.', '202108', 'Regular', '', '1', '', 'GRGraduate', 'UPPRUpper Division');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseCreditMaximumValue`, `CourseLevel`, `CourseAttribute`) VALUES ('', 'ECE', '30010', 'Intro To Mach Learn & Pat Rec', 'Intro To Mach Learn & Pat Rec', 'Credit Hours: 3.00.  Intelligent information processing, search and retrieval, classification, recognition, prediction and optimization with machine learning and pattern recognition algorithms such as neural networks, support vector machines, decision trees and data mining methods, current models and architectures, implementation topics especially in software, applications in areas such as information processing, search and retrieval of internet data, forecasting (prediction), classification, signal/image processing, pattern recognition, optimization, simulation, system identification, communications, control, management and finance. Topics covered will also be illustrated with the software package MATLAB and related toolboxes. Permission of instructor required.', '202108', 'Regular', '', '3', '', 'GRGraduate', 'UPPRUpper Division');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseCreditMaximumValue`, `CourseLevel`, `CourseAttribute`) VALUES ('', 'ECE', '30100', 'Signals And Systems', 'Signals And Systems', 'Credit Hours: 3.00.  Classification, analysis and design of systems in both the time- and frequency-domains. Continuous-time linear systems: Fourier Series, Fourier Transform, bilateral Laplace Transform. Discrete-time linear systems: difference equations, Discrete-Time Fourier Transform, bilateral Z-Transform. Sampling, quantization, and discrete-time processing of continuous-time signals. Discrete-time nonlinear systems: median-type filters, threshold decomposition. System design examples such as the compact disc player and AM radio.', '202108', 'Regular', '', '3', '', 'GRGraduate', 'UPPRUpper Division');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseCreditMaximumValue`, `CourseLevel`, `CourseAttribute`) VALUES ('', 'ECE', '30200', 'Probabilistic Methods', 'Probabilistic Methods', 'Credit Hours: 3.00.  An introductory treatment including probability of events, discrete and continuous random variables, multiple random variables, sums of random variables and long-term averages, and elementary random processes.  Applications involving uniform, Gaussian, exponential, geometric and related random variables. Introduction to parameter estimation and hypothesis testing. Discussion of wide-sense stationary random processes, including correlation functions, spectral densities and the response of linear time invariant systems. Course examples are drawn from signal processing, wireless communications, system reliability, and data science.', '202108', 'Regular', '', '3', '', 'GRGraduate', 'UPPRUpper Division');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseCreditMaximumValue`, `CourseLevel`, `CourseAttribute`) VALUES ('', 'ECE', '30300', 'Engineering Software Design', 'Engineering Software Design', 'Credit Hours: 3.00.  The purpose of this course is to introduce a variety of software and hardware design topics to Electrical and Computer engineering students, with an emphasis on problem solving. Topics include the interfaces of sensors and actuators, Unix shell script programming, LabVIEW graphic programming, as well as programmable logic controller (PLC) programming.', '202308', 'Regular', '', '0', '3', 'UGUndergraduate', 'UPPRUpper Division');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseCreditMaximumValue`, `CourseLevel`, `CourseAttribute`) VALUES ('', 'ECE', '30411', 'Electromagnetics I', 'Electromagnetics I', 'Credit Hours: 3.00.  This course is a continued study of vector calculus, electrostatics, magnetostatics, and Maxwell\'s Equations. It serves as an introduction to electromagnetic waves and transmission lines, which is continued in ECE 30412.', '202208', 'Regular', '', '3', '', 'GRGraduate', 'UPPRUpper Division');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseCreditMaximumValue`, `CourseLevel`, `CourseAttribute`) VALUES ('', 'ECE', '30412', 'Electromagnetics II', 'Electromagnetics II', 'Credit Hours: 3.00.  Electromagnetics II builds on Electromagnetics I (ECE 31100) and  emphasizes time-varying electromagnetic fields. Both fundamental understanding and an appreciation for applications that span all technologies related to electrical and computer engineering are emphasized. The topics covered include:  Maxwell\'s equations, plane waves, transmission lines, waveguides and cavities, and antennas and radiation. Applications addressed relate to photonics, communications, and imaging and sensing. More generally, illustrations cover the basic principles on which devices and systems used every day operate.', '202108', 'Regular', '', '3', '', 'PRProfessional', 'UPPRUpper Division');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseCreditMaximumValue`, `CourseLevel`, `CourseAttribute`) VALUES ('', 'ECE', '30414', 'Elmt Fiber Opt/Lasers/Optoelec', 'Elmt Fiber Opt/Lasers/Optoelec', 'Credit Hours: 3.00.  Fundamental of photonics, guided-wave optics, optical fibers, lasers, photon detectors, integrated optical components, optical information processing, devices in communication and sensor applications. Topics include generation, transformation, modulation and detection of laser beams and their applications.', '202108', 'Regular', '', '3', '', 'PRProfessional', 'UPPRUpper Division');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseCreditMaximumValue`, `CourseLevel`, `CourseAttribute`) VALUES ('', 'ECE', '30415', 'Fiber Optics And Lasers Lab', 'Fiber Optics And Lasers Lab', 'Credit Hours: 1.00.  This laboratory course exercises in lasers, modulation of laser beams, fiber components and systems. It covers some simple optical measurements like Power and beam spot measurement of Gaussian beams. Students learn about lens-pinhole spatial filters and beam expanders. Fourier transform, convolution and correlation are covered. Characteristics of LEDs and ILDs are shown. Students become familiar with optical fibers related experiments like Fiber-optic microbend intensity sensors, Electrooptic modulation, Fiber optic Mach-Zehnder interferometer etc. Students also learn how to build laser systems based on the knowledge acquired from previous labs and with some simple set up. Finally there is a final project where students can design a practical optical instrument based on their knowledge from the lab.', '202108', 'Regular', '', '1', '', 'PRProfessional', 'UPPRUpper Division');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseCreditMaximumValue`, `CourseLevel`, `CourseAttribute`) VALUES ('', 'ECE', '30416', 'Basics Of Engineering Optics', 'Basics Of Engineering Optics', 'Credit Hours: 3.00.  Basic control over propagation, reflection, refraction of optical radiation are covered. Applications to optical instrumentation such as microscopy, polarization optics such as wave plates, thin films, and holography are discussed. Geometrical optics including lenses, mirrors, prisms; Huygens\' principle, Fermat principle; rays; incoherent light. Physical Optics including gratings, interferometers, diffraction elements, polarizers; Huygens-Fresnel principle. Waves, amplitude and phase, coherent light. Law of reflection and refraction, interference, diffraction, polarization.', '202108', 'Regular', '', '3', '', 'PRProfessional', 'UPPRUpper Division');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseCreditMaximumValue`, `CourseLevel`, `CourseAttribute`) VALUES ('', 'ECE', '30417', 'Engineering Optics Laboratory', 'Engineering Optics Laboratory', 'Credit Hours: 1.00.  A set of laboratory experiments dealing with fundamentals and basic applications of geometrical optics, beams, polarization optics, wave optics and Fourier optics. Permission of instructor required.', '202108', 'Regular', '', '1', '', 'PRProfessional', 'UPPRUpper Division');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseCreditMaximumValue`, `CourseLevel`, `CourseAttribute`) VALUES ('', 'ECE', '30500', 'Semiconductor Devices', 'Semiconductor Devices', 'Credit Hours: 3.00.  Introduces and explains terminology, models, properties, and concepts associated with semiconductor devices. Provides detailed insight into the internal workings of the "building-block" device structures such as the pn-junction diode, Schottky diode, BJT, and MOSFET. Presents information about a wide variety of other devices including solar cells, LEDs, HBTs, and modern field-effect devices. Systematically develops the analytical tools needed to solve practical device problems.', '202108', 'Regular', '', '3', '', 'GRGraduate', 'UPPRUpper Division');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseCreditMaximumValue`, `CourseLevel`, `CourseAttribute`) VALUES ('', 'ECE', '30600', 'Circuits & Systems Lab', 'Circuits & Systems Lab', 'Credit Hours: 1.00.  Experiments in electronic circuits and systems, including spectral analysis techniques, sampling, distortion measurements, random signals, signal-to-noise ratio and correlation.', '202108', 'Regular', '', '1', '', 'GRGraduate', 'UPPRUpper Division');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseCreditMaximumValue`, `CourseLevel`, `CourseAttribute`) VALUES ('', 'ECE', '30653', 'Intro To Nano, Qntm Sci & Tech', 'Intro To Nano, Qntm Sci & Tech', 'Credit Hours: 3.00.  This interdisciplinary course offers an introduction to nanotechnology and quantum science and technology for undergraduate students in science and engineering. The students will develop understanding of interdisciplinary nature of these fields and utilize concepts in physics, chemistry and mechanics to describe and analyze unique properties of quantum and nanoscale objects. The course will provide the opportunity to get exposed to highly interdisciplinary nature of today\'s most active and rapidly expanding fields of research and technologies.', '202105', 'Regular', '', '3', '', 'PRProfessional', 'UPPRUpper Division');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseCreditMaximumValue`, `CourseLevel`, `CourseAttribute`) VALUES ('', 'ECE', '30700', 'Fields & Waves Lab', 'Fields & Waves Lab', 'Credit Hours: 1.00.  Experimental exercises illustrating concepts in electric and magnetic fields, transmission lines, electromagnetic fields, simple waveguides, and antennas.', '202108', 'Regular', '', '1', '', 'GRGraduate', 'UPPRUpper Division');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseCreditMaximumValue`, `CourseLevel`, `CourseAttribute`) VALUES ('', 'ECE', '30800', 'Sysm Simul & Contr Lab', 'Sysm Simul & Contr Lab', 'Credit Hours: 1.00.  Instruction and laboratory exercises in the solution of differential equations that arise in the modeling of physical systems. Instruction in the principles of operation and design of linear control systems.', '202108', 'Regular', '', '1', '', 'GRGraduate', 'UPPRUpper Division');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseCreditMaximumValue`, `CourseLevel`, `CourseAttribute`) VALUES ('', 'ECE', '30834', 'Fund Of Computer Graphics', 'Fund Of Computer Graphics', 'Credit Hours: 3.00.  Fundamental principles and techniques of computer graphics. The course covers the basics of going from a scene representation to a raster image using OpenGL. Specific topics include coordinate manipulations, perspective, basics of illumination and shading, color models, texture maps, clipping and basic raster algorithms, fundamentals of scene constructions. Permission of department required.', '202108', 'Regular', '', '0', '3', 'GRGraduate', 'UPPRUpper Division');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseCreditMaximumValue`, `CourseLevel`, `CourseAttribute`) VALUES ('', 'ECE', '30862', 'Obj Orient Prog C++ &Java', 'Obj Orient Prog C++ &Java', 'Credit Hours: 3.00.  C++ and Java programming languages, including classes, inheritance, encapsulation, polymorphism, class derivation, abstract classes, interfaces, static class members, object construction and destruction, namespaces, exception handling, function, overloading and overriding, function name overload resolution, container classes, and template classes.', '202108', 'Regular', '', '3', '', 'GRGraduate', 'UPPRUpper Division');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseCreditMaximumValue`, `CourseLevel`, `CourseAttribute`) VALUES ('', 'ECE', '30864', 'Software Engineering Tools', 'Software Engineering Tools', 'Credit Hours: 1.00.  This course will acquaint students with the toolkit of the modern software engineer. Students will learn the tools surrounding the software application itself, e.g., tools for software process, software construction, and software deployment. Examples include: integrated development editors (IDEs), version control systems, project management, bug tracking, scripting languages, testing aids, deployment technologies.', '202308', 'Regular', '', '0', '1', 'PRProfessional', 'UPPRUpper Division');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseCreditMaximumValue`, `CourseLevel`, `CourseAttribute`) VALUES ('', 'ECE', '31000', 'Motor Engine Control', 'Motor Engine Control', 'Credit hours: 3.00.  This course provides students with an introduction to electrical motors, generators, and related electrical and mechanical devices used in industrial applications. Topics include the design and applications of transformers, semiconductor amplification and switching devices, solid state relays, semiconductor input devices, programmable logic controllers, and motor drives.', '202308', 'Regular', '', '3', '', 'GRGraduate', 'UPPRUpper Division');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseCreditMaximumValue`, `CourseLevel`, `CourseAttribute`) VALUES ('', 'ECE', '31032', 'Power Systems Engineering', 'Power Systems Engineering', 'Credit Hours: 3.00.  Introduction to the economic operation of power systems, three-phase circuit analysis, modeling of transformers and transmission lines, steady-state network analysis using a power flow, analysis during faults and state estimation.', '202108', 'Regular', '', '3', '', 'PRProfessional', 'UPPRUpper Division');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseCreditMaximumValue`, `CourseLevel`, `CourseAttribute`) VALUES ('', 'ECE', '31033', 'Power Electronics', 'Power Electronics', 'Credit Hours: 3.00.  Introduction to the fundamental operating principles of power conditioning circuits that are currently being used to effect power flow from AC to DC and vice versa. Emphasis is on the relationship between form and function of these circuits. Circuits discussed will include AC/DC line-commutated converters, DC/DC converters, DC/variable frequency converters, resonant converters, and AC/AC converters. Computer simulations will be used as a part of the coursework.', '202305', 'Regular', '', '3', '', 'GRGraduate', 'UPPRUpper Division');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseCreditMaximumValue`, `CourseLevel`, `CourseAttribute`) VALUES ('', 'ECE', '31100', 'Elec & Magnetic Fields', 'Elec & Magnetic Fields', 'Credit Hours: 3.00.  Continued study of vector calculus, electrostatics, and magnetostatics, and Maxwell\'s equations. Introduction to electromagnetic waves, transmission lines, and radiation from antennas.', '202108', 'Regular', '', '3', '', 'GRGraduate', 'UPPRUpper Division');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseCreditMaximumValue`, `CourseLevel`, `CourseAttribute`) VALUES ('', 'ECE', '31200', 'Engr Economics & Project Mgmt', 'Engr Economics & Project Mgmt', 'Credit Hours: 3.00.  (ME 31100). Introduction to principles of engineering project management and techniques. Topics include technical feasibility studies, project specifications, scheduling, validation, lifecycle costing, and economic analysis. The focus is on managing an engineering project through scheduling, budgeting, resource management, execution and control.', '202108', 'Regular', '', '3', '', 'GRGraduate', 'UPPRUpper Division');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseCreditMaximumValue`, `CourseLevel`, `CourseAttribute`) VALUES ('', 'ECE', '31300', 'Energy Conversion Laboratory', 'Energy Conversion Laboratory', 'Credit Hours: 1.00.  Laboratory experiments in energy conversion including operation, testing, and applications of energy conversion machines including AC and DC motors and generators; experiments on magnetic circuits and transformers.', '202108', 'Regular', '', '1', '', 'UGUndergraduate', 'UPPRUpper Division');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseCreditMaximumValue`, `CourseLevel`, `CourseAttribute`) VALUES ('', 'ECE', '31500', 'Fund Electrical Energy Engr', 'Fund Electrical Energy Engr', 'Credit Hours: 3.00.  Resistive circuit analysis with controlled sources. Sinusoidal frequency response, filters and Bode plots. Complex power in AC circuits, ideal transformers and three-phase power. Power electronic circuits including diodes, transistor switches, rectifiers and AC-DC converters. Magnetic circuits, magnetic materials and B-H curves. Transformer equivalent circuit models. No credit will be given for ECE majors.', '202108', 'Regular', '', '3', '', 'PRProfessional', 'UPPRUpper Division');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseCreditMaximumValue`, `CourseLevel`, `CourseAttribute`) VALUES ('', 'ECE', '32100', 'Electromech Motion Dev', 'Electromech Motion Dev', 'Credit Hours: 3.00.  The general theory of electromechanical motion devices relating electric variables and electromagnetic forces. The basic concepts and operational behavior of DC, induction, brushless DC, and stepper motors used in control applications are presented.', '202108', 'Regular', '', '3', '', 'GRGraduate', 'UPPRUpper Division');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseCreditMaximumValue`, `CourseLevel`, `CourseAttribute`) VALUES ('', 'ECE', '32300', 'Elec-Mech Mot Dev Lab', 'Elec-Mech Mot Dev Lab', 'Credit Hours: 1.00.  Experiments closely coordinated with ECE 32100 involving measurement of fundamental parameters of various electromechanical devices using modern instrumentation techniques. Computer simulation is used to predict steady-state and dynamic operating characteristics. Comparison of predicted and measured performance is emphasized.', '202108', 'Regular', '', '1', '', 'GRGraduate', 'UPPRUpper Division');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseCreditMaximumValue`, `CourseLevel`, `CourseAttribute`) VALUES ('', 'ECE', '32400', 'Intro Energy Systems', 'Intro Energy Systems', 'Credit Hours: 3.00.  In this course, fundamentals of electrical machines, power circuit analysis techniques, concepts including torque, speed, DC machine equivalent circuit, synchronous and asynchronous AC machines, rotating fields, application of electronics on electrical machines, smart grids and their applications in power engineering, use of composite materials in energy applications, and alternative energy methods including solar energy.', '202108', 'Regular', '', '3', '', 'GRGraduate', 'UPPRUpper Division');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseCreditMaximumValue`, `CourseLevel`, `CourseAttribute`) VALUES ('', 'ECE', '32600', 'Engineering Project Management', 'Engineering Project Management', 'Credit Hours: 3.00.  (ME 32600)  Project management is an important skill that is needed in the private and public sectors as well as specialty businesses. This course will explore the challenges facing today\'s project managers and will provide a broad understanding of the project management environment focused on multiple aspects of the project.', '202108', 'Regular', '', '3', '', 'GRGraduate', 'UPPRUpper Division');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseCreditMaximumValue`, `CourseLevel`, `CourseAttribute`) VALUES ('', 'ECE', '32700', 'Engineering Economics', 'Engineering Economics', 'Credit Hours: 3.00.  (ME 32700)  Engineering Economics is designed as an overview of economics with a focus on how it relates to the practice of engineering. Topics include interest formulas, rate of return, life cost analysis, depreciation, taxes, and cash flow.', '202108', 'Regular', '', '3', '', 'GRGraduate', 'UPPRUpper Division');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseCreditMaximumValue`, `CourseLevel`, `CourseAttribute`) VALUES ('', 'ECE', '33300', 'Automatic Control Systems', 'Automatic Control Systems', 'Credit Hours: 3.00.  (ME 33300)  Analysis and design of control systems, from modeling and computer solutions to stability and performance issues with an orientation toward electrical and mechanical systems. Classical control system concepts are emphasized but an introduction to modern techniques is also provided.', '202308', 'Regular', '', '3', '', 'GRGraduate', 'UPPRUpper Division');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseCreditMaximumValue`, `CourseLevel`, `CourseAttribute`) VALUES ('', 'ECE', '33500', 'Electronics-Systems', 'Electronics-Systems', 'Credit Hours: 3.00.  Topics in multistage amplifiers, feedback amplifiers, oscillators, operational amplifiers, analog systems, power amplifiers and systems, communication systems.', '202108', 'Regular', '', '0', '3', 'GRGraduate', 'UPPRUpper Division');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseCreditMaximumValue`, `CourseLevel`, `CourseAttribute`) VALUES ('', 'ECE', '33700', 'ASIC Design Lab', 'ASIC Design Lab', 'Credit Hours: 2.00.  Introduction to standard cell design of Application Specific Integrated Circuits (ASICs) using modern hardware description languages (HDLs). Emphasis on how to write HDL code that will map readily to hardware. Laboratory experiments using commercial grade computer-aided design (CAD) tools for HDL based design, logic simulation, automatic placement and routing, timing analysis and verification.', '202108', 'Regular', '', '0', '2', 'GRGraduate', 'UPPRUpper Division');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseCreditMaximumValue`, `CourseLevel`, `CourseAttribute`) VALUES ('', 'ECE', '34000', 'Simu Modeling & Ident', 'Simu Modeling & Ident', 'Credit Hours: 3.00.  Investigation and evaluation of design problems through simulation of systems described by ordinary differential and difference equations. Development of simulation models from physical parameters and from experimental data. Topics include continuous, discrete, and hybrid models of electrical, mechanical, and biological systems. Laboratory experiences demonstrate concepts studied in text and lecture.', '202108', 'Regular', '', '0', '3', 'GRGraduate', 'UPPRUpper Division');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseCreditMaximumValue`, `CourseLevel`, `CourseAttribute`) VALUES ('', 'ECE', '35100', 'Software Engineering', 'Software Engineering', 'Credit Hours: 3.00.  This course is designed to give students in Computer Engineering group design project experience in the engineering of large scale software. Tools and techniques for requirements analysis, resource estimation, project management, design, implementation, testing, quality assurance, and maintenance will be used in projects that simulate real-world software engineering work environments.', '202108', 'Regular', '', '3', '', 'GRGraduate', 'UPPRUpper Division');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseCreditMaximumValue`, `CourseLevel`, `CourseAttribute`) VALUES ('', 'ECE', '35400', 'Software Engr Design I', 'Software Engr Design I', 'Credit Hours: 3.00.  The design and implementation of larger scale software in Java.  Introduction of software engineering design concepts.  Application of fundamental concepts and programming strategies useful in the context of any programming language.', '202108', 'Regular', '', '3', '', 'GRGraduate', 'UPPRUpper Division');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseCreditMaximumValue`, `CourseLevel`, `CourseAttribute`) VALUES ('', 'ECE', '35700', 'VLSI Chip Design Lab', 'VLSI Chip Design Lab', 'Credit Hours: 1.00.  Introduction to standard cell design of VLSI digital circuits using VHDL hardware description language. Emphasis on how to write VHDL that will map readily to hardware. Laboratory experiments using commercial grade computer-aided design (CAD) tools for VHDL based design, schematic based logic entry, logic and VHDL simulation, automatic placement and routing, timing analysis, and testing.', '202108', 'Regular', '', '1', '', 'GRGraduate', 'UPPRUpper Division');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseCreditMaximumValue`, `CourseLevel`, `CourseAttribute`) VALUES ('', 'ECE', '35800', 'Intro To VHDL', 'Intro To VHDL', 'Credit Hours: 3.00.  Introduction to the design of digital systems using VHDL hardware description language. Emphasis on how to write VHDL that will map readily to hardware. Projects assigned using commercial grade computer-aided design (CAD) tools for VHDL based design, VHDL simulation and synthesis.', '202108', 'Regular', '', '3', '', 'GRGraduate', 'UPPRUpper Division');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseCreditMaximumValue`, `CourseLevel`, `CourseAttribute`) VALUES ('', 'ECE', '35900', 'C And Data Structures', 'C And Data Structures', 'Credit Hours: 3.00.  An introductory level course on C, a general purpose high-level language with features to facilitate such tasks as systems programming and structuring of data. Students becoming proficient in C language programming will learn techniques of structured programming data structures and how to develop programs that are used regularly in many applications.', '202108', 'Regular', '', '0', '3', 'GRGraduate', 'UPPRUpper Division');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseCreditMaximumValue`, `CourseLevel`, `CourseAttribute`) VALUES ('', 'ECE', '36200', 'Micropro Sys & Intrfac', 'Micropro Sys & Intrfac', 'Credit Hours: 4.00.  An introduction to basic computer organization, microprocessor instruction sets, assembly language programming, and microcontroller peripherals.', '202108', 'Regular', '', '0', '4', 'GRGraduate', 'UPPRUpper Division');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseCreditMaximumValue`, `CourseLevel`, `CourseAttribute`) VALUES ('', 'ECE', '36201', 'Micro System Design & Interfac', 'Micro System Design & Interfac', 'Credit Hours: 4.00.  An introduction to basic computer organization, microcontroller instruction sets, microcontroller programming using assembly and C language, microcontroller interfacing and peripherals, and embedded system design.', '202108', 'Regular', '', '0', '4', 'PRProfessional', 'UPPRUpper Division');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseCreditMaximumValue`, `CourseLevel`, `CourseAttribute`) VALUES ('', 'ECE', '36400', 'Sftwr Engr Tools Lab', 'Sftwr Engr Tools Lab', 'Credit Hours: 1.00.  To acquaint the students with a variety of current software engineering tools, scripting languages, and application programming languages. Students are expected to use their previous programming experience to design and test software programs using the techniques learned in this course.', '202308', 'Regular', '', '0', '1', 'GRGraduate', 'UPPRUpper Division');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseCreditMaximumValue`, `CourseLevel`, `CourseAttribute`) VALUES ('', 'ECE', '36500', 'Digital Comp Design', 'Digital Comp Design', 'Credit Hours: 3.00.  The hardware organization of computer systems including the following topics: instruction set selection, arithmetic/logic unit design, hard-wired and microprogrammed control schemes, memory organization, IO interface design. The course will involve computer simulation of digital systems.', '202108', 'Regular', '', '3', '', 'GRGraduate', 'UPPRUpper Division');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseCreditMaximumValue`, `CourseLevel`, `CourseAttribute`) VALUES ('', 'ECE', '36800', 'Data Structures', 'Data Structures', 'Credit Hours: 3.00.  Provides insight into the use of data structures. Topics include stacks, queues and lists, trees, graphs, sorting, searching, and hashing.', '202108', 'Regular', '', '3', '', 'GRGraduate', 'UPPRUpper Division');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseCreditMaximumValue`, `CourseLevel`, `CourseAttribute`) VALUES ('', 'ECE', '36900', 'Disc Math For Comp Eng', 'Disc Math For Comp Eng', 'Credit Hours: 3.00.  This course introduces discrete mathematical structures and finite-state machines. Students will learn how to use logical and mathematical formalisms to formulate and solve problems in computer engineering. Topics include formal logic, proof techniques, recurrence relations, sets, combinatorics, relations, functions, algebraic structures, and finite-state machines.', '202108', 'Regular', '', '3', '', 'GRGraduate', 'UPPRUpper Division');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseCreditMaximumValue`, `CourseLevel`, `CourseAttribute`) VALUES ('', 'ECE', '37100', 'Comptr Organiz & Design', 'Comptr Organiz & Design', 'Credit Hours: 3.00.  Design of computer systems with emphasis on computer hardware. Topics discussed include: Introduction to Basic Design Concepts, Computer Abstraction and Technology, Role of Performance, Instruction Language, Arithmetic for Computers, Processor Data Path and Control, Enhancing Performance with Pipelining, and Exploiting Memory Hierarchy. Students design and implement a RISC processor in the laboratory.', '202108', 'Regular', '', '0', '3', 'GRGraduate', 'UPPRUpper Division');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseCreditMaximumValue`, `CourseLevel`, `CourseAttribute`) VALUES ('', 'ECE', '37200', 'Principals Of Software Design', 'Principals Of Software Design', 'Credit Hours: 3.00.  (CSCI 36300) This course is designed to teach students best practices in designing and implementing object-oriented systems of high quality. To accomplish this task, we start with an overview of software design patterns and their role in developing high-quality software. We then begin surveying different design-level software design patterns, such as the Bridge, Strategy, Wrapper Facade, and Visitor software design patterns. Next, we touch on software design patterns for building distributed systems. Finally, we finish the course by surveying software anti-patterns, which are common design mistakes that negatively impact system quality, such as degrading performance as the system scales in size and complexity. Students will have the opportunity to apply learned techniques on several programming projects throughout the semester.', '202108', 'Regular', '', '3', '', 'PRProfessional', 'UPPRUpper Division');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseCreditMaximumValue`, `CourseLevel`, `CourseAttribute`) VALUES ('', 'ECE', '37300', 'Numerical Method Engrs', 'Numerical Method Engrs', 'Credit Hours: 3.00.  Introduction to numerical methods for engineers. Topics include solution methods for nonlinear algebraic equations, sets of linear and nonlinear algebraic equations, eigenvalue problems, interpolation and curve fitting, numerical differentiation and integration, and techniques to solve ordinary and partial differential equations.', '202108', 'Regular', '', '3', '', 'GRGraduate', 'UPPRUpper Division');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseCreditMaximumValue`, `CourseLevel`, `CourseAttribute`) VALUES ('', 'ECE', '37900', 'Junior Part In VIP In ECE', 'Junior Part In VIP In ECE', 'Credit Hours: 1.00 or 2.00.  This course provides an opportunity for undergraduate students to explore and develop comprehensive applications of electrical and computer engineering technologies, especially as they relate to active research areas of Purdue faculty members. Students will learn about the underlying research, and will work on teams to formulate applications of the research that address real-world needs. Students will attend a weekly lecture that provides an introduction to a broad range of applicable technologies and development tools - some associated with the activities of specific teams, and some addressing topics of more general value to students enrolled in the course.', '202108', 'Regular', '', '0', '2', 'GRGraduate', 'UPPRUpper Division');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseCreditMaximumValue`, `CourseLevel`, `CourseAttribute`) VALUES ('', 'ECE', '38000', 'Computer Engr Analysis', 'Computer Engr Analysis', 'Credit Hours: 3.00.  Theory and application of computers in simulation, data acquisition control, instrumentation, and in the solution of engineering problems. Development of mathematical models suitable for computer solutions, and numerical techniques. Traditional and modern software such as FORTRAN, C, LabVIEW, MATLAB, Lotus 1-2-3, and Excel will be used.', '202108', 'Regular', '', '0', '3', 'GRGraduate', 'UPPRUpper Division');
