INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '10000', 'Intro To C S', 'Intro To C S', 'Credit Hours: 1.00.  This course is intended to: integrate freshman computer science majors into the department; help them adjust to university life; assist them in developing their academic and intellectual capabilities; introduce them to contemporary issues in computer science; provide an overview of the careers open to those with degrees in computer science. This course must be taken pass/no pass only. Credit by examination is not available for this course.', '202108', 'Regular', '', '1', 'GRGraduate', 'LOWRLower Division', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '10100', 'Digital Literacy', 'Digital Literacy', 'Credit Hours: 3.00.  Survey of the digital world, computers as multi-purpose machines, digital information, definition of programming, computers everywhere (the Internet of things), computers that perform simultaneous computations, how apps work, data storage and searching, databases, digital audio, graphics, video, computer networks and the Internet, the World Wide Web and Internet sharing services, network and Internet performance, real-time services, artificial intelligence, cloud computing, security, and privacy. CS students may take course for elective credit only.', '202108', 'Regular', '', '3', 'PRProfessional', 'LOWRLower Division', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '10200', 'Computer Sc Concepts', 'Computer Sc Concepts', 'Credit Hours: 3.00.  This course emphasizes computing concepts of special interest to first-year students. Hands-on applications are stressed.', '202108', 'Regular', '', '3', 'GRGraduate', 'LOWRLower Division', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '10500', 'Computer Science Success Sem', 'Computer Science Success Sem', 'Credit Hours: 1.00.  This course supports students in their transition to Computer Science programs. It provides an overview of what the programs in the CS department entail, the expectations, and guidelines on how to be successful. This class sets a baseline for professionalism as a student and a future employee while fostering connections between peers, campus, departments, faculty, and research. Students shall learn applications of the degree programs and the various opportunities for advancement as a CS student.', '202108', 'Regular', '', '1', 'PRProfessional', 'LOWRLower Division', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '10700', 'Intro Cmptr Sci Majors', 'Intro Cmptr Sci Majors', 'Credit Hours: 3.00.  A general introduction to computers and their applications. A fundamental understanding of Information Systems, components, terminology, programming concepts and representative applications. Contemporary issues. Hands-on experience with operating systems; information gathering systems; word processing; spreadsheets, including presentation graphics and statistical functions database systems; statistical packages; and programming principles a final, capstone project required.', '202108', 'Regular', '', '0', 'GRGraduate', 'LOWRLower Division', '3');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '11200', 'Computer Science For Everyone', 'Computer Science For Everyone', 'Credit Hours: 3.00.  This course is designed to provide a broad and realistic idea of what computer professionals do and how they do it. Designed to be accessible to all students, it will prepare them for later computing courses, including software development courses. The course may introduce programming concepts and programming languages. Students will be introduced to various professional opportunities and work environments. Current topics in computer science as they relate to society and automation will be covered. Students will leave the course with a basic understanding and appreciation of automation and computer science.', '202108', 'Regular', '', '0', 'GRGraduate', 'LOWRLower Division', '3');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '11400', 'Intro To Visual Programming', 'Intro To Visual Programming', 'Credit Hours: 3.00.  This course introduces programming using a visual approach. Students will learn the syntax and structure of an object-oriented programming language. They will develop stand-alone, event-driven, graphical user interface (GUI) applications for personal computer use.', '202108', 'Regular', '', '0', 'GRGraduate', 'LOWRLower Division', '3');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '12300', 'Programming I: Java', 'Programming I: Java', 'Credit Hours: 4.00.  Introduction to the fundamental concepts of programming and problem solving using the Java programming language. Topics include variables, expressions, operators, data types, control structures, methods, objects, arrays, classes, simple I/O, testing, and debugging strategies. This course provides a foundation for more advanced programming techniques, including object-oriented programming and design.', '202308', 'Regular', '', '0', 'GRGraduate', 'LOWRLower Division', '4');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '12400', 'Prog II: Object-Oriented Java', 'Prog II: Object-Oriented Java', 'Credit Hours: 4.00.  A continuation of CS 12300 which focuses on object-oriented programming along with the fundamentals of object-oriented design. Topics include classes, encapsulation, inheritance, polymorphism, abstract classes, interfaces, class hierarchies, generics, streams, exceptions, abstraction, problem decomposition, collection classes, iterators, GUI\'s, and event-driven programs.', '202308', 'Regular', '', '0', 'GRGraduate', 'LOWRLower Division', '4');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '12600', 'Systems Programming In C', 'Systems Programming In C', 'Credit Hours: 3.00.  This course is an introduction to programming and using a computer system. The programming languages used are C and the command-line shell. The C topics include pointers, arrays, structs, unions, memory allocation and management, memory layout, program structure, multiple source files, data representation, system calls and library functions, buffers, file abstraction and access, profiling. The command-line topics include file and directory structures, permissions, I/O redirection, pipes, environment variables, command-line utilities, remote access, version control, simple shell scripts.', '202308', 'Regular', '', '3', 'PRProfessional', 'LOWRLower Division', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '14000', 'Intro To DP (VBasic)', 'Intro To DP (VBasic)', 'Credit Hours: 3.00.  Not available for credit toward graduation in the School of Science or for computer technology majors. Intended for students who expect to use computers outside the physical sciences and engineering. Introduction to the Visual Basic programming language and the development of event-driven programs.', '202108', 'Regular', '', '3', 'GRGraduate', 'LOWRLower Division', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '15500', 'Cobol Programming', 'Cobol Programming', 'Credit Hours: 3.00.  Study of the COBOL programming language with emphasis on file organization and processing. Topics covered include data types, data definition, subprograms and parameter passing. Emphasis on developing program structure and style.', '202108', 'Regular', '', '3', 'GRGraduate', 'LOWRLower Division', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '15800', 'C Programming', 'C Programming', 'Credit Hours: 3.00.  Introduction to structured programming in C. Data types and expression evaluation. Programmer-defined functions including passing parameters by value and by address. Selection topics include if/else/else-if, conditional expressions, and switch. Repetition topics include while, do-while, for, and recursion. External file input and output. Arrays, analysis of searching and sorting algorithms, and strings. Pointers and dynamic memory allocation. Students are expected to complete assignments in a collaborative environment. CS 15800 may be used to satisfy College of Science requirement of participation in at least one team-building and collaboration experience. Credit cannot be obtained for both CS 15900 and any of CS 15600, CS 15800 and CS 18000.', '202108', 'Regular', '', '0', 'GRGraduate', 'LOWRLower Division', '3');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '15900', 'C Programming', 'C Programming', 'Credit Hours: 3.00.  Fundamental principles, concepts, and methods of programming in C, with emphasis on applications in the physical sciences and engineering. Basic problem solving and programming techniques; fundamental algorithms and data structures; and use of programming logic in solving engineering problems. Students are expected to complete assignments in a collaborative learning environment. Credit cannot be obtained for both CS 15900 and any of CS 15600, CS 15800 and CS 18000.', '202308', 'Regular', '', '0', 'GRGraduate', 'LOWRLower Division', '3');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '16000', 'Intro To Comp Sci I', 'Intro To Comp Sci I', 'Credit Hours: 4.00.  An introduction to the fundamental concepts and techniques of Computer Science. Students will learn to program using an object-oriented language. They will learn how to translate a real problem into a program description, and how to write and test a program to implement their description. The emphasis will be on developing a professional style at an elementary level. CS 16000 will carry syntax as far as interacting classes, arrays of one dimension, and simple file i/o.  Students with no programming background should instead consider CS 11200.', '202108', 'Regular', '', '0', 'GRGraduate', 'LOWRLower Division', '4');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '16100', 'Intro To Comp Sci II', 'Intro To Comp Sci II', 'Credit Hours: 4.00.  This course continues CS 16000.  Students will design larger programs to solve more complicated problems. The emphasis is on deepening students\' abilities to deal with abstraction, problem decomposition, and the interaction between program components. Students will develop their professional practice through analysis of more general problems, debugging and testing of their programs, and written presentation of their solutions. Topics include multidimensional arrays, event-driven programs, GUI\'s, class inheritance and interfaces, and libraries.', '202108', 'Regular', '', '0', 'GRGraduate', 'LOWRLower Division', '4');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '17000', 'C And Data Structures', 'C And Data Structures', 'Credit Hours: 3.00.  The course will introduce the C programming language including the language syntax, the programming environment, basic data types, complex data types (pointer, array, structures, bit fields and unions), macros, i/o, and functions. Program development will emphasize modularization, data abstraction, and selection and analysis of algorithms. Other topics include recursion, files, linked lists, stacks, queues, and binary trees. Course projects are related to common engineering and computer science applications. Course will not count toward graduation in the computer science department.', '202108', 'Regular', '', '3', 'GRGraduate', 'LOWRLower Division', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '17200', 'Introduction To C', 'Introduction To C', 'Credit Hours: 1.00.  The course will introduce the C programming language including the language syntax, the programming environment, basic data types, complex data types (pointer, array, structures, bit fields and unions), macros, i/o, and functions.', '202108', 'Regular', '', '1', 'GRGraduate', 'LOWRLower Division', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '17600', 'Data Engineering In Python', 'Data Engineering In Python', 'Credit Hours: 3.00.  The course introduces students to programming fundamentals in Python, including loops, functions and different data types, and provides an introduction to data engineering including  working with common data formats and learning the basics of data wrangling. Students will format, extract, clean, filter, transform, search, combine, summarize, aggregate, and visualize a diverse range of data sets.  Python libraries including MatPlotLib and Pandas are used.', '202208', 'Regular', '', '0', 'UGUndergraduate', 'LOWRLower Division', '3');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '17700', 'Progrmng With MM Objs', 'Progrmng With MM Objs', 'Credit Hours: 4.00.  Introduction to computers and programming: number representations, primitive data types and operations, basic control structures, programming applets and applications using graphical user interfaces, programming for detecting events and performing actions, processing multimedia objects such as images and sounds. Throughout the course, examples are drawn from a variety of fields in the natural sciences. Not open to CS majors with a grade of C or better in CS 18000.  Not open to non-CS majors with a grade of C or better in any course in computer programming.', '202108', 'Regular', '', '0', 'GRGraduate', 'LOWRLower Division', '4');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '18000', 'Prob Solving & O-O Programming', 'Prob Solving & O-O Programming', 'Credit Hours: 4.00.  Problem solving and algorithms, implementation of algorithms in a high level programming language, conditionals, the iterative approach and debugging, collections of data, searching and sorting, solving problems by decomposition, the object-oriented approach, subclasses of existing classes, handling exceptions that occur when the program is running, graphical user interfaces (GUIs), data stored in files, abstract data types, a glimpse at topics from other CS courses.  Intended primarily for students majoring in computer sciences. Credit cannot be obtained for both CS 18000 and any of 15600, 15800 and 15900. Not open to students with credit in CS 24000.', '202108', 'Regular', '', '0', 'GRGraduate', 'LOWRLower Division', '4');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '18200', 'Foundations Of Comp Sc', 'Foundations Of Comp Sc', 'Credit Hours: 3.00.  Logic and proofs; sets, functions, relations, sequences and summations; number representations; counting; fundamentals of the analysis of algorithms; graphs and trees; proof techniques; recursion; Boolean logic; finite state machines; pushdown automata; computability and undecidability.', '202108', 'Regular', '', '0', 'GRGraduate', 'LOWRLower Division', '3');
