INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '19100', 'Freshm Resources Semnr', 'Freshm Resources Semnr', 'Credit Hours: 1.00.  This course is intended to integrate freshman majors in computer sciences into the department, help them adjust to university life, and assist them in developing academic and intellectual survival skills. Weekly recitation sections enable the students to work in teams. Strongly recommended for freshmen. The credit may be used only toward free electives.', '202108', 'Regular', '', '0', 'GRGraduate', 'LOWRLower Division', '1');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '19300', 'Tools', 'Tools', 'Credit Hours: 1.00.  This course is designed to introduce new Computer Science students to various programming tools which will aid them in their Computer Science classes, personal projects, and software development job experiences.', '202108', 'Regular', '', '0', 'PRProfessional', 'LOWRLower Division', '1');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '19700', 'Freshman Honors Semnr', 'Freshman Honors Semnr', 'Credit Hours: 1.00.  A seminar dealing with the history, context, and future of computer science. Open only to students in the Computer Sciences Honors Program. The credit may be used only toward free electives.', '202108', 'Regular', '', '1', 'GRGraduate', 'LOWRLower Division', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '20200', 'Rpg Programming', 'Rpg Programming', 'Credit Hours: 1.00.  Programming of representative business applications using the RPG (Report Programming Generator) programming language. Emphasis on reports, external data handling and files.', '202108', 'Regular', '', '1', 'GRGraduate', 'LOWRLower Division', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '20300', 'Adv Visual Programming', 'Adv Visual Programming', 'Credit Hours: 3.00.  This course continues the study of visual programming begun in IST 14000. Students will create multi-tier, event-driven applications using object-oriented approaches and databases. Students will also create web applications. Students will also be introduced to data structures.', '202108', 'Regular', '', '3', 'GRGraduate', 'LOWRLower Division', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '20600', 'Comp Alg And Prog', 'Comp Alg And Prog', 'Credit Hours: 3.00.  Using a computer algebra system to solve mathematics problems, learning how to translate mathematical notation and procedures into the language of the computer algebra system. Learning the basic concepts of programming languages, comparing programming concepts with mathematical concepts. Typically offered Fall Spring Summer.', '202108', 'Regular', '', '3', 'GRGraduate', 'LOWRLower Division', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '21100', 'Competitive Programming I', 'Competitive Programming I', 'Credit Hours: 2.00.  CP1 teaches applied algorithmic ideas and problem solving techniques to solve programming interview and competitive programming questions including usage of basic data structures such as [array, set, map, stack, queue, deque, priority queue], the four main algorithm paradigms: [complete search, greedy, divide and conquer, dynamic programming], other algorithmic ideas including [binary search the answer/bisection, meet-in-the-middle, prefix sum and difference arrays, two pointers, sliding window], and basic graph algorithms covering [strongly/connected components, floodfill, topological sort, shortest paths].', '202308', 'Regular', '', '2', 'GRGraduate', 'LOWRLower Division', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '22300', 'Comp Arch & Assem Lang', 'Comp Arch & Assem Lang', 'Credit Hours: 3.00.  An introduction to the fundamental concepts of computer architecture progressing from the digital logic level to the microarchitecture level and then to the instruction set level. Assembly language and the assembly process will also be included.', '202308', 'Regular', '', '3', 'GRGraduate', 'LOWRLower Division', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '22700', 'Introduction To C Programming', 'Introduction To C Programming', 'Credit Hours: 2.00.  Introduction to programming in C to solve engineering problems. Topics covered include primitive data types, control structures, standard input/output, file input/output, mathematic library, procedural programming, problem-solving, user-defined functions, arrays, and pointers.', '202108', 'Regular', '', '2', 'PRProfessional', 'LOWRLower Division', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '22800', 'Object Oriented Program In C++', 'Object Oriented Program In C++', 'Credit Hours: 1.00.  This course will introduce the fundamentals of objected oriented programming in C++.  Students should gain understanding of the implementation of inheritance, composition, method overloading and overriding, polymorphism, templates, and standard template library.', '202108', 'Regular', '', '1', 'GRGraduate', 'LOWRLower Division', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '22900', 'C/C++ Programming', 'C/C++ Programming', 'Credit Hours: 4.00.  This course will introduce the programming in C and fundamentals of object-oriented programming in C++ to solve engineering problems. This course introduces the C programming language features in the beginning of the semester. Topics covered include data types, control structures, standard input/output, file input/output, mathematic library, problem-solving, functions, arrays, dynamic memory, and pointers. Then the course provides an introduction to object-oriented programming using C++ language. Students will gain understanding of data abstractions, classes, inheritance, composition, method overloading and overriding, generic programming, and standard template library. Students are expected to complete lab assignments and several programming assignments.', '202108', 'Regular', '', '0', 'PRProfessional', 'LOWRLower Division', '4');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '23200', 'Intro to C and Unix', 'Intro to C and Unix', 'Credit Hours: 3.00.  This course is an introduction to the C language and the Unix operating system.  It presumes fluency in a high-level language.  The course will focus on standard C and Unix tools, rather than a proprietary version of either.  C topics include:  data types, the syntax for arithmetic, logical and relational functions, control functions, scope, communications with the shell, file I/O, pointers, arrays, structs, typedefs, macro and preprocessor functions, and the use of libraries and multiple source files.  Unix topics include the file and directory structures, permissions, shells, standard tools such as history, sort, vi, grep, sed, tar, and make, and simple shell scripting.', '202108', 'Regular', '', '3', 'GRGraduate', 'LOWRLower Division', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '23500', 'Intro Organizatnl Comp', 'Intro Organizatnl Comp', 'Credit Hours: 3.00.  People and organizations, decision-making, information systems, telecommunications, desktop systems, integration tools, collaboration and groupware, multimedia, authoring multimedia documents, emerging technologies. May not be taken for credit by Computer Science majors.', '202108', 'Regular', '', '0', 'GRGraduate', 'LOWRLower Division', '3');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '24000', 'Programming In C', 'Programming In C', 'Credit Hours: 3.00.  The UNIX environment, C development cycle, data representation, operators, program structure, recursion, macros, C preprocessor, pointers and addresses, dynamic memory allocation, structures, unions, typedef, bit-fields, pointer/structure applications, UNIX file abstraction, file access, low-level I/O, concurrency.', '202108', 'Regular', '', '0', 'GRGraduate', 'LOWRLower Division', '3');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '24200', 'Introduction To Data Science', 'Introduction To Data Science', 'Credit Hours: 3.00.  (STAT 24200) This course provides a broad introduction to the field of data science. The course focuses on using computational methods and statistical techniques to analyze massive amounts of data and to extract knowledge. It provides an overview of foundational computational and statistical tools for data acquisition and cleaning, data management and big data systems. The course surveys the complete data science process from data to knowledge and gives students hands-on experience with tools and methods. Basic knowledge of Python required. Computer Science majors cannot count this course as a degree requirement but can take it for credit as a free elective if taken before CS 37300, 34800, 47100, 47300, 44800.', '202108', 'Regular', '', '0', 'UGUndergraduate', 'LOWRLower Division', '3');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '25000', 'Computer Architecture', 'Computer Architecture', 'Credit Hours: 4.00.  Digital logic: transistors, gates, and combinatorial circuits; clocks; registers and register banks; arithmetic-logic units; data representation: big-endian and little-endian integers; ones and twos complement arithmetic; signed and unsigned values; Von-Neumann architecture and bottleneck; instruction sets; RISC and CISC designs; instruction pipelines and stalls; rearranging code; memory and address spaces; physical and virtual memory; interleaving; page tables; memory caches; bus architecture; polling and interrupts; DMA; device programming; assembly language; optimizations; parallelism; data pipelining.', '202108', 'Regular', '', '0', 'GRGraduate', 'LOWRLower Division', '4');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '25100', 'Data Structures And Algorithms', 'Data Structures And Algorithms', 'Credit Hours: 3.00.  Running time analysis of algorithms and their implementations, one-dimensional data structures, trees, heaps, additional sorting algorithms, binary search trees, hash tables, graphs, directed graphs, weighted graph algorithms, additional topics.', '202108', 'Regular', '', '0', 'GRGraduate', 'LOWRLower Division', '3');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '25200', 'Systems Programming', 'Systems Programming', 'Credit Hours: 4.00.  Low-level programming; review of addresses, pointers, memory layout, and data representation; text, data, and bss segments; debugging and hex dumps; concurrent execution with threads and processes; address spaces; file names; descriptors and file pointers; inheritance; system calls and library functions; standard I/O and string libraries; simplified socket programming; building tools to help programmers; make and make files; shell scripts and quoting; unix tools including sed, echo, test, and find; scripting languages such as awk; version control; object and executable files (.o and a.out); symbol tables; pointers to functions; hierarchical directories; and DNS hierarchy; programming embedded systems.', '202108', 'Regular', '', '0', 'GRGraduate', 'LOWRLower Division', '4');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '25600', 'Appl Software Project', 'Appl Software Project', 'Credit Hours: 3.00.  Project course requiring implementation of an integrated application system from a structured design. Emphasis on structured development techniques and on system and user documentation. Other topics include indexed and relative file organization, JCL, Reportwriter, and introduction to information and business systems.', '202108', 'Regular', '', '3', 'GRGraduate', 'LOWRLower Division', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '26000', 'Data Structures', 'Data Structures', 'Credit Hours: 3.00.  This course is an introduction to the common data structures (ADT\'s) of computer science and the algorithms which maintain and operate on them. These include arrays, stacks, queues, linked lists, trees, graphs, and more general structures like maps and dictionaries. The relevant algorithms include additions, deletions, sorts, searches, traversals, and others appropriate to the structure. The course includes an introduction to the prediction and testing of algorithm performance.', '202108', 'Regular', '', '0', 'GRGraduate', 'LOWRLower Division', '3');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '27000', 'Assembly Language', 'Assembly Language', 'Credit Hours: 3.00.  Computer hardware organization, the fetch-execute cycle, machine language and data representation. Assembly language programming, addressing techniques, input/output, calling conventions, procedures and parameter passing, macros, and conditional assembly. Assembler concepts. Emphasis on the development of well structured programs in assembly language.', '202108', 'Regular', '', '3', 'GRGraduate', 'LOWRLower Division', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '27100', 'Computer Architecture', 'Computer Architecture', 'Credit Hours: 3.00.  Introduction to computer organization and architecture. Fundamentals of digital logic and representation of numeric and nonnumeric data. Assembly level organization and programming, including instruction formats, addressing modes, and subprogram call/return.  Design of main memory, cache memory, and virtual memory.  Interrupt basics, interrupt-driven I/O, DMA, and bus protocols. Processor organization, data paths, the control unit, microprogramming, pipelining, and performance enhancements. Multiprocessor and alternative architectures.', '202108', 'Regular', '', '3', 'GRGraduate', 'LOWRLower Division', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '27400', 'Data Communications', 'Data Communications', 'Credit Hours: 3.00.  A survey of data communication techniques. Topics include communications media, synchronous and asynchronous transmission, coding, error detection and correction, communications protocols and formats, modulation and demodulation, multiplexing and networking, and the OSI model with emphasis on the physical and data link layers.', '202108', 'Regular', '', '3', 'GRGraduate', 'LOWRLower Division', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '27500', 'Data Structures', 'Data Structures', 'Credit Hours: 3.00.  Data structures describe the way that computer programs organize and store information. This course introduces the specification, representation and manipulation of the basic data structures common to much of computer programming such as: linked lists, arrays, stacks, queues, strings, trees, graphs, search trees, heaps, hash tables, and B-trees.', '202108', 'Regular', '', '3', 'GRGraduate', 'LOWRLower Division', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '28000', 'Surv Information Tech', 'Surv Information Tech', 'Credit Hours: 3.00.  An introduction to information technology development from a perspective using business fundamentals that relate to information systems and the analysis and design of those systems. Topics include competitive, strategic, and technological advantages; collaborative partnerships within e-business; decision making; and databases. Supply chain management, customer relationship management, enterprise resource planning, and other applications will be surveyed.', '202108', 'Regular', '', '3', 'GRGraduate', 'LOWRLower Division', '');
