INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '29500', 'Industrial Practicum', 'Industrial Practicum', 'Credit Hours: 1.00.  Practical problems in local industry limited to about 10 to 20 hours per week for which the student may receive some re-numeration. May be repeated but the total combined credit that may be applied to a degree is limited to three. Open only to full-time students.', '202108', 'Regular', '', '1', 'GRGraduate', 'LOWRLower Division', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '30100', 'Language Competency', 'Language Competency', 'Credit Hours: 1.00.  A course intended to give the student experience in an additional high-level language. Each section of this course is the responsibility of a particular faculty member who will advise the student and assign programming projects. A student may receive credit for at most three of these sections, but for no section whose language was a major component of a course for which credit has already been attained.', '202108', 'Regular', '', '1', 'GRGraduate', 'UPPRUpper Division', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '30200', 'Operating Systems', 'Operating Systems', 'Credit Hours: 3.00.  An operating system manages all of the hardware and software resources of a computer. This course provides an introduction to the basic concepts and terminology of operating systems. Topics will include multiprogramming, CPU scheduling, memory management, file systems, concurrent processes, multiprocessors, security, and network operating systems.', '202108', 'Regular', '', '3', 'GRGraduate', 'UPPRUpper Division', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '30303', 'Intern In CS', 'Intern In CS', 'Credit Hours: 1.00 to 3.00.  Directed in-service experience with employers that may include but not limited to government agencies, private industry, and community organizations. Pre-approval of the department required before internship has begun.', '202208', 'Regular', '', '1', 'GRGraduate', 'UPPRUpper Division', '3');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '30600', 'Computers In Society', 'Computers In Society', 'Credit Hours: 3.00.  Case study analysis of the social impacts of computerization and networking. Topics include computer ethics, crime, privacy, security, reliability, and vulnerability. Other topics include cyberphilia, cyberphobia, censorship, depersonalization, disenfranchisement, automated decision making, artificial intelligence, cognitive science, and ergonomics. Students present projects applying these issues to today\'s environment.', '202108', 'Regular', '', '3', 'GRGraduate', 'UPPRUpper Division', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '30700', 'Software Engineerng I', 'Software Engineerng I', 'Credit Hours: 3.00.  An introduction to the methods and tools of software engineering; software life cycle; specification and design of software, software testing, cost and effort estimation; laboratory exercises with design, testing, and other tools.', '202108', 'Regular', '', '0', 'GRGraduate', 'UPPRUpper Division', '3');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '30900', 'Discrete Math Struct', 'Discrete Math Struct', 'Credit Hours: 3.00.  This course is the study of finite and discrete mathematical structures relating to the theory of computation. Topics will include directed and undirected graphs and their relation to these structures, combinatorial problems inherent in computation, Boolean algebra, and recurrence relations.', '202108', 'Regular', '', '3', 'GRGraduate', 'UPPRUpper Division', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '31000', 'Topics Comp Languages', 'Topics Comp Languages', 'Credit Hours: 3.00.  Study of data structures including two-dimensional arrays, pointer variables, linked lists, stacks, queues and trees. Brief introduction to other high-level programming languages. Algorithms for searching and sorting. Recursion. Not open to majors in the department of computer science.', '202108', 'Regular', '', '3', 'GRGraduate', 'UPPRUpper Division', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '31100', 'Competitive Programming II', 'Competitive Programming II', 'Credit Hours: 2.00.  CP2 teaches experienced programmers additional techniques to solve interview and competitive programming problems and builds on material learned in CP1. This includes specific algorithmic techniques such as [shortest paths, topological sort, MST, union find, range queries], advanced algorithms surrounding trees and DAGs, advanced problem types in [dynamic programming, backtracking/simulation, mathematics, string processing], and more. It can be viewed as a programming complement to CS 38100, with some overlap in content.', '202308', 'Regular', '', '2', 'GRGraduate', 'UPPRUpper Division', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '31400', 'Numerical Methods', 'Numerical Methods', 'Credit Hours: 3.00.  Iterative methods for solving nonlinear equations; direct and iterative methods for solving linear systems; approximations of functions, derivatives, and integrals; error analysis.', '202108', 'Regular', '', '3', 'GRGraduate', 'UPPRUpper Division', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '31600', 'Programming Languages', 'Programming Languages', 'Credit Hours: 3.00.  The study of programming language features and their implementation in different types of programming languages. The design goals and motivations for various languages will be discussed. Topics will include a comparison of block-structured, object-oriented, functional, and logic programming languages. The advantages and disadvantages of each type of language will be considered. Specific examples of each type of language will be included.', '202108', 'Regular', '', '3', 'GRGraduate', 'UPPRUpper Division', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '32100', 'Intro To Comp Graphics', 'Intro To Comp Graphics', 'Credit Hours: 3.00.  This course is an introductory course in computer graphics. This course introduces fundamental concepts of computer graphics technology and principles to create three-dimensional graphics. Fundamental graphics algorithms are discussed, as well as graphics programming, using a modern graphics standard. Students are expected to complete several programming assignments that implement fundamental computer graphics techniques in the Unix operating system environment.', '202108', 'Regular', '', '3', 'GRGraduate', 'UPPRUpper Division', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '33100', 'Intro To C++ & OOP', 'Intro To C++ & OOP', 'Credit Hours: 3.00.  An introduction to the C++ language with emphasis on features supporting object oriented programming. Fundamental data type and operations. Expression evaluation. Selection and iteration constraints. Functions, procedures, and macro. Standard libraries. Classes:  declaration and definition; instances; member functions; constructors and destructors; function overloading; inheritance and polymorphism. Stream input and output. Using classes to encapsulate data structure and implementation details.', '202108', 'Regular', '', '3', 'GRGraduate', 'UPPRUpper Division', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '33200', 'Algorithms', 'Algorithms', 'Credit Hours: 3.00.  An algorithm is a procedure for solving a problem in a finite number of steps. Algorithms, along with data structures, form the fundamental building blocks of computer programs. The types of algorithms discussed will include sorting, searching, probabilistic, graph, and geometric algorithms. The following algorithm techniques are covered: backtracking, divide and conquer, branch and bound, greedy method, and dynamic programming.', '202108', 'Regular', '', '3', 'GRGraduate', 'UPPRUpper Division', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '33400', 'Fund Of Computer Graph', 'Fund Of Computer Graph', 'Credit Hours: 3.00.  Fundamental principles and techniques of computer graphics. The course covers the basics of going from a scene representation to a raster image using OpenGL. Specific topics include coordinate manipulations, perspective, basics of illumination and shading, color models, texture maps, clipping and basic raster algorithms, fundamentals of scene constructions. C S 314 is recommended.', '202108', 'Regular', '', '0', 'GRGraduate', 'UPPRUpper Division', '3');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '33600', 'Network Programming', 'Network Programming', 'Credit Hours: 3.00.  This course is an introduction to computer networks and the programming of network applications. This course will emphasize using the Sockets API to implement application layer protocols, especially the HTTP protocol used by web servers. In addition, the course will cover network abstraction layers from a programmer\'s perspective, explaining what is needed in order to write correct, reliable network programs.', '202308', 'Regular', '', '3', 'PRProfessional', 'UPPRUpper Division', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '34200', 'Comp Bio-Med Image An', 'Comp Bio-Med Image An', 'Credit Hours: 4.00.  Introduction to image, manipulation and analysis. Biomedical materials to be analyzed include electrophoretic gels, bacterial agar plates, cells and tissues, x-ray films and CAT scan images. Personal computer systems and the basic programming skill of the C language also will be introduced.', '202108', 'Regular', '', '4', 'GRGraduate', 'UPPRUpper Division', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '34800', 'Information Systems', 'Information Systems', 'Credit Hours: 3.00.  File organization and index structures; object-oriented database languages; the relational database model with introductions to SQL and DBMS; hierarchical models and network models with introductions to HDDL, HDML, and DBTG Codasyl; data mining; data warehousing; database connectivity; distributed databases; the client/server paradigm; middleware, including ODBC, JDBC, CORBA, and MOM.', '202108', 'Regular', '', '0', 'GRGraduate', 'UPPRUpper Division', '3');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '35000', 'Prog Language Design', 'Prog Language Design', 'Credit Hours: 3.00.  A survey of language design issues and their implications for translation and run-time support. Examination of modern programming languages and features: Abstract data and control structures, procedures, parameter passing mechanisms, block structuring and scope rules, input/output, concurrent execution and storage management. Models of run time behavior. Comparison of imperative and declarative programming languages.', '202108', 'Regular', '', '3', 'GRGraduate', 'UPPRUpper Division', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '35100', 'Cloud Computing', 'Cloud Computing', 'Credit Hours: 3.00.  Introduction to cloud computing. Cloud data center infrastructure, public and private clouds, servers and virtualized servers, data center networks and virtualized networks, virtualized disk, file, and object storage, the concept of scale-out, algorithms and programming systems used in the design and implementation of cloud-native software, especially using containers and orchestration systems, microservices, serverless designs, controller-based designs, edge and fog computing, security in a cloud environment, programming models.', '202208', 'Regular', '', '0', 'GRGraduate', 'UPPRUpper Division', '3');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '35200', 'Compilers Prin & Pract', 'Compilers Prin & Pract', 'Credit Hours: 3.00.  Should not be taken concurrently with CS 354. The theory and practice of programming language translation, compilation, and run-time systems, organized around a significant programming project to build a compiler for a simple but nontrivial programming language. Modules, interfaces, tools. Data structures for tree languages. Lexical analysis, syntax analysis, abstract syntax. Symbol tables, semantic analysis. Translation, intermediate code, basic blocks, traces. Instruction selection, CISC and RISC machines. Liveness analysis, graph coloring register allocation. Supplemental material drawn from garbage collection, object-oriented languages, higher-order languages, dataflow analysis, optimization, polymorphism, scheduling and pipelining, memory hierarchies.', '202108', 'Regular', '', '0', 'GRGraduate', 'UPPRUpper Division', '3');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '35300', 'Prin Concurrency & Parallelism', 'Prin Concurrency & Parallelism', 'Credit Hours: 3.00.  Important concepts, models, algorithms, abstractions, and implementation aspects of concurrent and parallel programs. Topics include: techniques used to describe concurrent programs (e.g., threads, events, co-routines, continuations), abstractions for shared-memory and message-passing programs, relaxed memory models, livelock and deadlock detection, lock-free algorithms, data races and atomicity, scheduling techniques, process calculi, and software transactions.', '202108', 'Regular', '', '3', 'GRGraduate', 'UPPRUpper Division', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '35400', 'Operating Systems', 'Operating Systems', 'Credit Hours: 3.00.  Should not be taken concurrently with CS 35200. Introduction to operating systems. Computer system and operating system architectures, processes, inter-process communication, inter-process synchronization, mutual exclusion, deadlocks, memory hierarchy, virtual memory, CPU scheduling, file systems, I/O device management, security.', '202108', 'Regular', '', '0', 'GRGraduate', 'UPPRUpper Division', '3');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '35500', 'Intro To Cryptography', 'Intro To Cryptography', 'Credit Hours: 3.00.  An introduction to cryptography basics: Classic historical ciphers including Caesar, Vigenere and Vernam ciphers; modern ciphers including DES, AES, Pohlig-Hellman, and RSA; signatures and digests; key exchange; simple protocols; block and stream ciphers; network-centric protocols.', '202108', 'Regular', '', '3', 'GRGraduate', 'UPPRUpper Division', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '36000', 'Software Engineering', 'Software Engineering', 'Credit Hours: 3.00.  This course provides an introduction to the methods of software engineering. Topics include the software development process, software specification using the UML for analysis and design, software metrics, quality assurance and test plans, and organizational and management issues.', '202308', 'Regular', '', '3', 'GRGraduate', 'UPPRUpper Division', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '36400', 'Intro To Database Sys', 'Intro To Database Sys', 'Credit Hours: 3.00.  Theory and application of database systems for information organization and retrieval based on the relational model. Includes database models, query languages, data dependencies, normal forms, and database design. Projects include use of commercial mainframe and microcomputer databases.', '202108', 'Regular', '', '3', 'GRGraduate', 'UPPRUpper Division', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '36500', 'Advanced Database Sys', 'Advanced Database Sys', 'Credit Hours: 3.00.  The first part of the course includes theory of SQL, implementation of some components of DBMS, and comprehensive project. The second part of the course includes such advanced topics as recovery; concurrency; and distributed, deductive, and knowledge databases.', '202108', 'Regular', '', '3', 'GRGraduate', 'UPPRUpper Division', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '36600', 'Struct Analysis Tech', 'Struct Analysis Tech', 'Credit Hours: 3.00.  Methods used in analyzing information systems. Topics include user interviewing and observation, event analysis, data flow diagrams, data dictionaries, mini-specifications, decision trees, decision tables, and both logical and physical models. Students practice these techniques in a major structured analysis project resulting in a requirements specification document.', '202108', 'Regular', '', '0', 'GRGraduate', 'UPPRUpper Division', '3');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '36700', 'Struct Design Techniq', 'Struct Design Techniq', 'Credit Hours: 3.00.  Methods used in designing information systems. Topics include structure charts, module specifications, pseudocode, coupling, cohesion, transform analysis, transaction analysis, and user interface design. Includes the detailed design of an information system and the implementation of a prototype of that design.', '202108', 'Regular', '', '0', 'GRGraduate', 'UPPRUpper Division', '3');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '36800', 'Human Comp Interaction', 'Human Comp Interaction', 'Credit Hours: 3.00.  Introduction to general issues surrounding human-computer interaction (HCI).  The course presents principles, design methodologies, tools, and evaluation techniques with an emphasis on human-centered interface design and implementation. Other issues covered include HCI aspects of multimedia systems, World Wide Web, computer-supported cooperative work, and recent paradigms of HCI.', '202108', 'Regular', '', '3', 'GRGraduate', 'UPPRUpper Division', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '37000', 'Systems Programming', 'Systems Programming', 'Credit Hours: 3.00.  Design and operation of systems programs including assemblers, loaders, linkers and macro-processors. Issues in language translation; expression evaluation, forward referencing, symbol table manipulation, data conversion and code generation. Lexical and syntactic analysis.', '202108', 'Regular', '', '3', 'GRGraduate', 'UPPRUpper Division', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '37200', 'Web App Development', 'Web App Development', 'Credit Hours: 3.00.  Introduction to web application development. Characteristics of web and application servers; web engineering principles and application architectures; web page construction; client and server-side scripting; database interaction; web application deployment and management; security and performance issues; overview of application-layer protocols.', '202108', 'Regular', '', '3', 'GRGraduate', 'UPPRUpper Division', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '37300', 'Data Mining & Machine Learning', 'Data Mining & Machine Learning', 'Credit Hours: 3.00.  This course will introduce students to the field of data mining and machine learning, which sits at the interface between statistics and computer science. Data mining and machine learning focuses on developing algorithms to automatically discover patterns and learn models of large datasets. This course introduces students to the process and main techniques in data mining and machine learning, including exploratory data analysis, predictive modeling, descriptive modeling, and evaluation.', '202108', 'Regular', '', '3', 'PRProfessional', 'UPPRUpper Division', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '37400', 'Computer Networks', 'Computer Networks', 'Credit Hours: 3.00.  The design and implementation of data communications networks. Topics include network topologies; message, circuit and packet switching; broadcast, satellite and local area networks; routing; the OSI model with emphasis on the network, transport and session layers.', '202108', 'Regular', '', '3', 'GRGraduate', 'UPPRUpper Division', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '37500', 'Multimedia Networking', 'Multimedia Networking', 'Credit Hours: 3.00.  This course is a survey of multimedia networks. Topics include multimedia information representation, text and image compression, audio and video compression, multimedia networking.', '202108', 'Regular', '', '3', 'PRProfessional', 'UPPRUpper Division', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '37600', 'Computer Architecture', 'Computer Architecture', 'Credit Hours: 3.00.  A comparative study of computer systems and hardware organization. Topics include processor and memory organization, microprogramming, architectural support for operating systems and high-level languages and I/O organization. Examples of representative architectures.', '202108', 'Regular', '', '3', 'GRGraduate', 'UPPRUpper Division', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '38000', 'Artificial Intelligence', 'Artificial Intelligence', 'Credit Hours: 3.00.  Fundamental concepts and techniques of artificial intelligence. Search techniques, including local search and constraint satisfaction. Knowledge representation concepts and methods of reasoning. Software agents, machine learning and neural networks, and AI planning systems.', '202108', 'Regular', '', '3', 'GRGraduate', 'UPPRUpper Division', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '38001', 'C++ Programming', 'C++ Programming', 'Credit Hours: 1.00.  This course builds on the CS 24000 (Programming In C) course to teach C++ extensions. This 5-week one-credit course teaches C++ programming and helps students improve their understanding of object-oriented programming. It also teaches how to use an Integrated Development Environment (IDE) when programming in C++.', '202108', 'Regular', '', '1', 'PRProfessional', 'UPPRUpper Division', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '38002', 'Advanced Java Programming', 'Advanced Java Programming', 'Credit Hours: 1.00.  This course builds on the Java course CS 18000 (Problem Solving And Object Oriented Programming) to teach advanced topics in Java. This 5-week one-credit course will teach advanced topics such as Network Programming in Java, JDBC, JNI, and J2EE to write web applications in Java. You will also improve your understanding of Object Oriented Programming.', '202108', 'Regular', '', '1', 'UGUndergraduate', 'UPPRUpper Division', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '38003', 'Python Programming', 'Python Programming', 'Credit Hours: 1.00.  This course teaches the Python programming language assuming that students have already taken a course in computer programming. This 5-week one-credit course teaches the Python language, the most common modules used in Python, as well as how to write Python web applications.', '202108', 'Regular', '', '0', 'PRProfessional', 'UPPRUpper Division', '1');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '38100', 'Intro Analysis Algor', 'Intro Analysis Algor', 'Credit Hours: 3.00.  Techniques for analyzing the time and space requirements of algorithms. Application of these techniques to sorting, searching, pattern-matching, graph problems, and other selected problems. Brief introduction to the intractable (NP-hard) problems.', '202205', 'Regular', '', '0', 'GRGraduate', 'UPPRUpper Division', '3');
