INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '39700', 'Honors Seminar', 'Honors Seminar', 'Credit Hours: 0.00.  A seminar for all sophomores, juniors, and seniors in the Computer Sciences Honors Program. Meets eight times each semester under the supervision of the Honors coordinator. The meetings focus on honors research projects, helping students to identify appropriate projects and form groups, and providing a forum for juniors and seniors to report on their projects as required in the honors program.', '202108', 'Regular', '', '0', 'GRGraduate', 'UPPRUpper Division', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '40400', 'Distributed Systems', 'Distributed Systems', 'Credit Hours: 3.00.  A distributed system is two or more computers working together as a single unit. These systems are essential to the understanding of present and future computer applications. This course will include the following topics: concurrent processing, threads, network programming, distributed file systems, remote procedure calls, sockets, distributed objects, client-server models, and Internet protocols.', '202108', 'Regular', '', '3', 'GRGraduate', 'UPPRUpper Division', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '40700', 'Software Engr Senior Project', 'Software Engr Senior Project', 'Credit Hours: 3.00.  A capstone course combining all the concepts students have learned in the Software Engineering Track: software and software engineering, working as a team leader and as a team member, Agile Software Development, Scrum Software Development, Version Control, basing software development on reusable technology, developing requirements (User Stories and Use Cases), modelling with classes, design patterns, focusing on users, modelling interactions and behavior, architecting and designing software, testing and inspecting, managing the software development process. This is a project course. Students are organized into 4-6 student teams. Each team completes a full-semester software engineering project. Each team creates a Project Charter, Product Backlog, Design, and for 3 Sprints -- Planning Document, Demo, Retrospective Document. The final demo is a formal presentation.', '202108', 'Regular', '', '0', 'GRGraduate', 'UPPRUpper Division', '3');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '40800', 'Software Testing', 'Software Testing', 'Credit Hours: 3.00.  Preliminaries: errors and testing; software quality, requirements, behavior, and correctness; testing, debugging, verification; control flow graphs, dominators; types of testing; Test selection: from requirements, finite state models, and combinatorial designs; regression testing and test minimization; Test adequacy assessment: control and data flow; mutation based; testing tools.', '202108', 'Regular', '', '3', 'GRGraduate', 'UPPRUpper Division', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '41000', 'Automat & Computabilty', 'Automat & Computabilty', 'Credit Hours: 3.00.  A finite automaton is a mathematical model for a computational system. Computer science embodies many examples of finite state systems. This course will cover the basic principles of deterministic and non-deterministic finite automata, Turing machines, formal language theory, regular expressions, context-free grammers, the halting problem, and unsolvability.', '202108', 'Regular', '', '3', 'GRGraduate', 'UPPRUpper Division', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '41100', 'Competitive Programming III', 'Competitive Programming III', 'Credit Hours: 2.00.  CP3 teaches experienced programmers additional techniques to solve competitive programming problems and builds on material learned in CP1 and CP2. This includes algorithmic techniques in topics such as [network flow, computational geometry, graph matching, NP-hard problems]. Primarily, CP3 prepares students to compete in programming contests, which means most class time is focused on simulating contest environments and teaching teamwork and communication alongside problem practice.', '202308', 'Regular', '', '2', 'GRGraduate', 'UPPRUpper Division', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '41600', 'Software Engineering', 'Software Engineering', 'Credit Hours: 3.00.  Software engineering is the study of the theory, methods, and tools which are needed to develop large, complex software systems. This course covers the specifications, design, documentation, implementation, and testing of software systems. Software life cycles, principles of project management, and case studies are covered. A group project will be assigned.', '202305', 'Regular', '', '3', 'GRGraduate', 'UPPRUpper Division', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '42000', 'Senior Design Project', 'Senior Design Project', 'Credit Hours: 3.00.  The objectives of this course is to provide students with concrete experience in writing advanced computer programs for practical application in science or industry. The student develops necessary software using appropriate techniques and prepares documentation for the use and support of the completed system.', '202108', 'Regular', '', '3', 'GRGraduate', 'UPPRUpper Division', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '42100', 'Advanced Comp Graphics', 'Advanced Comp Graphics', 'Credit Hours: 3.00.  Advanced topics in computer graphics such as three-dimensional rendering, curve and surface design, anti-aliasing, animation, and visualization. Other topics will be selected depending on current research trends. Through development of projects, students will gain practical experience about modern computer graphics.', '202108', 'Regular', '', '3', 'GRGraduate', 'UPPRUpper Division', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '42200', 'Computer Networks', 'Computer Networks', 'Credit Hours: 3.00.  Undergraduate-level introduction to computer networks and data communication. Low-level details of media, signals, and bits: time division and frequency division multiplexing; encoding; modulation; bandwidth, throughput, and noise. Packet transmission: Local Area Network (Ethernet, FDDI) and Wide Area Network technologies (ATM); wireless networks; network interconnection with repeaters, bridges, and switches; DSU/CSU; xDSL and cable modems. Internetworking: router-based architecture; IP addressing; address binding with ARP; datagram encapsulation and fragmentation; UDP and TCP, retransmission; protocol ports; ICMP and error handling. Network applications: client/server concept; port demultiplexing; program interface to protocols (API); use by clients and servers; domain name system; TELNET; Web technologies including HTTP, CGI, Java; RPC and middleware; network management.', '202108', 'Regular', '', '0', 'GRGraduate', 'UPPRUpper Division', '3');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '42600', 'Computer Security', 'Computer Security', 'Credit Hours: 3.00.  A survey of the fundamentals of information security. Risks and vulnerabilities, policy formation, controls and protection methods, database security, encryption, authentication technologies, host-based and network-based security issues, personnel and physical security issues, issues of law and privacy.', '202108', 'Regular', '', '0', 'GRGraduate', 'UPPRUpper Division', '3');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '43400', 'Adv Computer Graphics', 'Adv Computer Graphics', 'Credit Hours: 3.00.  Advanced concepts and techniques of computer graphics. The course covers, in complete detail, going from a scene representation to a raster image without using OpenGL or other graphics packages. The course develops a complete graphics implementation in which the students implement every aspect of the graphics pipeline. This involves a substantial software project in C/C++.', '202108', 'Regular', '', '0', 'GRGraduate', 'UPPRUpper Division', '3');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '43900', 'Intro To Data Visualization', 'Intro To Data Visualization', 'Credit Hours: 3.00.  The course offers an introduction to the fundamentals principles, design strategies, and techniques needed to visually communicate, explore, and analyze data. The course focuses primarily on the visual representation of inherently non-spatial data (e.g., tables and spreadsheets, graphs and networks, trees, text, and time series), but also considers the visualization of maps and of data in geospatial context.', '202308', 'Regular', '', '0', 'PRProfessional', 'UPPRUpper Division', '3');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '44000', 'Large Scale Data Analytics', 'Large Scale Data Analytics', 'Credit Hours: 3.00.  This course provides an integrated view of the key concepts of modern algorithmic data analytics. It focuses on teaching principles and methods needed to analyze large datasets in order to extract novel, transformative insights for the underlying application. The course emphasizes the duality between formulating questions that can be answered by statistical data analysis tools (the statistical perspective) and the algorithmic challenge of actually extracting such answers using available parallel and distributed computational resources from massive datasets. The topics cover three areas: (1) algorithmic concepts necessary for big data analytics, (2) bid data systems, including data management and programming, and (3) advanced analytic methods to address characteristics of real-world big data problems.', '202108', 'Regular', '', '0', 'PRProfessional', 'UPPRUpper Division', '3');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '44200', 'Database Systems', 'Database Systems', 'Credit Hours: 3.00.  A database is a system whose purpose is to organize, retrieve, and maintain large amounts of information. This course introduces the concepts and structures used in designing and implementing database systems. Topics include hierarchical network, relational, and object-oriented data models, database design principles, normalization, data dictionaries, query languages and processing.', '202108', 'Regular', '', '3', 'GRGraduate', 'UPPRUpper Division', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '44500', 'Computer Security', 'Computer Security', 'Credit Hours: 3.00.  A survey of the fundamentals of computer security. Topics include risks and vulnerabilities, policy formation, controls and protection methods, survey of malicious logic, database security, encryption, authentication, intrusion detection, network and system security issues, personnel and physical security issues, security design principles, issues of law and privacy.', '202201', 'Regular', '', '3', 'GRGraduate', 'UPPRUpper Division', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '44800', 'Intro Relational Db Sy', 'Intro Relational Db Sy', 'Credit Hours: 3.00.  An in-depth examination of relational database systems including theory and concepts as well as practical issues in relational databases. Modern database technologies such as object-relational and Web-based access to relational databases. Conceptual design and entity relationship modeling, relational algebra and calculus, data definition and manipulation languages using SQL, schema and view management, query processing and optimization, transaction management, security, privacy, integrity management.', '202208', 'Regular', '', '0', 'GRGraduate', 'UPPRUpper Division', '3');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '45500', 'Computer Graphics', 'Computer Graphics', 'Credit Hours: 3.00.  Computer graphics provides a mechanism for creating and manipulating images by means of a computer. This course covers two-dimensional curve drawing, view transformations, geometric modeling, projects, ray tracing, surface patch, three-dimensional object rendering, shading, and animation. Windows programming using OpenGL and MFC will also be introduced.', '202108', 'Regular', '', '3', 'GRGraduate', 'UPPRUpper Division', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '45600', 'Programming Languages', 'Programming Languages', 'Credit Hours: 3.00.  Concepts for structuring data, computation, and whole programs. Object-oriented languages, functional languages, logic- and rule-based languages. Data types, type checking, exception handling, concurrent processes, synchronization, modularity, encapsulation, interfaces, separate compilation, inheritance, polymorphism, dynamic binding, subtyping, overloading, beta-reduction, unification.', '202108', 'Regular', '', '0', 'GRGraduate', 'UPPRUpper Division', '3');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '45700', 'Introduction To Data Mining', 'Introduction To Data Mining', 'Credit Hours: 3.00.  Data mining refers to the process of automatic discovery of patterns and knowledge from large data sets. As an introductory course on data mining, this course presents the knowledge discovery process, and introduces data preprocessing and exploration, major data mining tasks, the relevant methodologies and techniques, and data mining applications from different disciplines.', '202108', 'Regular', '', '3', 'PRProfessional', 'UPPRUpper Division', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '46000', 'Senior Capstone Project I', 'Senior Capstone Project I', 'Credit Hours: 3.00.  The first course of a two-semester sequence. Student teams will participate in the development of a substantial application-oriented or research-oriented software project utilizing a formal software process model. Emphasis on teamwork, project management, and oral and written communication. Student teams will conduct review activities and develop artifacts appropriate for the software project and process model chosen.', '202108', 'Regular', '', '3', 'GRGraduate', 'UPPRUpper Division', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '46200', 'Intro Artfcl Intlgnce', 'Intro Artfcl Intlgnce', 'Credit Hours: 3.00.  This course covers the fundamental concepts and techniques of artificial intelligence. The topics include intelligent agents and search algorithms (both uninformed and informed search), planning and decision making, supervised and unsupervised machine learning algorithms (e.g., KNN, decision trees, random forest, linear and logistic Regression, clustering, etc.), and applications of AI in NLP, robotics, computer vision, and other fields.', '202308', 'Regular', '', '3', 'GRGraduate', 'UPPRUpper Division', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '46400', 'Computer Sys Planning', 'Computer Sys Planning', 'Credit Hours: 3.00.  The planning and design of computer systems, including the formulation of corporate requirements, configuration of hardware to satisfy stated requirements, comparison and evaluation of equipment, installation considerations and implementation procedures.', '202108', 'Regular', '', '3', 'GRGraduate', 'UPPRUpper Division', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '46500', 'Senior Capstone Project II', 'Senior Capstone Project II', 'Credit Hours: 3.00.  The second course of a two-semester sequence. Student teams will complete the development of a substantial application-oriented or research-oriented software project begun in CS 46000. Emphasis on teamwork, project management, and oral and written communication. Student teams will conduct review activities and develop artifacts appropriate for the software project and process model chosen. Students will be required to conduct a final formal review and demonstration to project stakeholders and other interested persons.', '202108', 'Regular', '', '3', 'GRGraduate', 'UPPRUpper Division', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '46600', 'Strat Iss Infor Systms', 'Strat Iss Infor Systms', 'Credit Hours: 3.00.  Topics in information systems management including strategic planning for competitive advantage, chargeback, systems portfolio risk analysis, security, and assimilating technology advances. Students develop an information systems strategic plan.', '202108', 'Regular', '', '3', 'GRGraduate', 'UPPRUpper Division', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '46700', 'Project Management', 'Project Management', 'Credit Hours: 3.00.  Covers the techniques required to manage systems development. Topics include project proposals, planning, estimating, organizing, controlling, and completion. Students practice these techniques on a major project using project management software.', '202108', 'Regular', '', '3', 'GRGraduate', 'UPPRUpper Division', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '47100', 'Intr To Artifcl Intlgn', 'Intr To Artifcl Intlgn', 'Credit Hours: 3.00.  Students are expected to spend at least three hours per week gaining experience with artificial intelligence systems and developing software. Basic problem-solving strategies, heuristic search, problem reduction and AND/OR graphs, knowledge representation, expert systems, generating explanations, uncertainty reasoning, game playing, planning, machine learning, computer vision, and programming systems such as Lisp or Prolog.', '202108', 'Regular', '', '0', 'GRGraduate', 'UPPRUpper Division', '3');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '47200', 'Opr Systems Design', 'Opr Systems Design', 'Credit Hours: 3.00.  The design and implementation of modern multiprocessing operating systems. Topics include concurrent programming, real and virtual storage allocation, resource allocation and deadlock prevention and avoidance, job scheduling, and analytic modeling. Students will complete projects involving concurrency and implement a portion of a multiprocessing operating system.', '202108', 'Regular', '', '3', 'GRGraduate', 'UPPRUpper Division', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '47300', 'Web Info Search & Management', 'Web Info Search & Management', 'Credit Hours: 3.00.  This course teaches important concepts and knowledge of information retrieval for managing unstructured data such as text data on Web or in emails. At the same time, students will be exposed to a large number of important applications. Students in the course will get hands on experience from homework and a course project. The first part of the course focuses on general concepts/techniques such as stemming, indexing, vector space model, and feedback procedure. The second part of the course shows how to apply the set of techniques on different applications such as Web search, text categorization, and information recommendation.', '202108', 'Regular', '', '3', 'GRGraduate', 'UPPRUpper Division', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '47400', 'Compiler Construction', 'Compiler Construction', 'Credit Hours: 3.00.  Techniques for the syntax-directed translation of modern high-level languages. Topics include: grammars and language specification, language design issues, lexical analysis, LL and LR parsing techniques, semantics, symbol table design, code generation and local optimization. Students are required to implement a compiler for a subset of a structured high-level language such as Pascal or Ada.', '202108', 'Regular', '', '3', 'GRGraduate', 'UPPRUpper Division', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '47500', 'Human-Computer Interaction', 'Human-Computer Interaction', 'Credit Hours: 3.00. The goal of this course is to teach students how to design useful and usable interactive systems that address important needs of people. Students will experience the entire user-centered design life cycle, from need finding to usability evaluation. Topics covered in the course include user-centered design principles, usability heuristics, need-finding methods such as semi-structured interviews and contextual inquiry, quick prototyping techniques, usability evaluation methods such as hallway testing and human-subjects user study, and theories about user interaction and decision making. As we are entering a new era of AI, the course will also include a brief introduction on how to apply the HCI principles and techniques to AI-powered systems. This course is project-based. Students will form project teams among themselves to work on a semester-long project and apply the user-centered design principles, theories, and techniques that they have learned in class to build a useful and usable interactive system such as a mobile application. This course is also highly interactive, including a series of design studios and in-class activities that require active participation, communication, and discussion with other students.', '202208', 'Regular', '', '3', 'PRProfessional', 'UPPRUpper Division', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '47800', 'Intro Bioinformatics', 'Intro Bioinformatics', 'Credit Hours: 3.00.  (BIOL 47800) Bioinformatics is broadly defined as the study of molecular biological information, targeting particularly the enormous volume of DNA sequence and functional complexity embedded in entire genomes. Topics will include understanding the evolutionary organization of genes (genomics), the structure and function of gene products (proteomics), and the dynamics of gene expression in biological processes (transcriptomics). Inherently, bioinformatics is interdisciplinary, melding various applications of computational science with biology. This jointly taught course introduces analytical methods from biology, statistics and computer science that are necessary for bioinformatics investigations. The course is intended for junior and senior undergraduates from various science backgrounds. Our objective is to develop the skills of both tool users and tool designers in this important new field of research.', '202108', 'Regular', '', '3', 'GRGraduate', 'UPPRUpper Division', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '48000', 'Practicum In C S', 'Practicum In C S', 'Credit Hours: 3.00.  The purpose of this course is to provide students an opportunity to obtain experience in software design, development, and related activities in computer science. Students will work on a real problem obtained in conjunction with business industries or other organizations. Not more than two terms of CS 48000 may be taken for credit. ', '202308', 'Regular', '', '0', 'GRGraduate', 'UPPRUpper Division', '3');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '48300', 'Theory Of Computation', 'Theory Of Computation', 'Credit Hours: 3.00.  Turing machines and the Church-Turing thesis; decidability; halting problem; reducibility; undecidable problems; decidability of logical theories; Kolmogorov complexity; time classes; P, NP, NP-complete; space classes; Savitch\'s theorem, PSPACE-completeness, NL-completeness; hierarchy theorems; approximation theorems; probabilistic algorithms; applications of complexity to parallel computation and cryptography.', '202108', 'Regular', '', '3', 'GRGraduate', 'UPPRUpper Division', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '48600', 'Analysis Of Algorithms', 'Analysis Of Algorithms', 'Credit Hours: 3.00.  Techniques for analyzing the time and space requirements of algorithms and problems. Application of these techniques to sorting, searching, pattern- matching, graph problems and other selected problems. Brief introduction to the intractable (NP-hard) problems.', '202108', 'Regular', '', '3', 'GRGraduate', 'UPPRUpper Division', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '48700', 'Professional Practice V', 'Professional Practice V', 'Credit Hours: 0.00.  Professional Practice. Permission of instructor required.', '202108', 'Regular', '', '0', 'GRGraduate', 'UPPRUpper Division', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '48800', 'Theory Of Computation', 'Theory Of Computation', 'Credit Hours: 3.00.  Mathematical models of computation including finite and pushdown automata and Turing machines and equivalence of different general-purpose models. Grammars and their relation to automata, Church\'s Thesis and limits of computation.', '202108', 'Regular', '', '3', 'GRGraduate', 'UPPRUpper Division', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '48900', 'Embedded Systems', 'Embedded Systems', 'Credit Hours: 3.00.  Software for embedded systems; programming with extreme constraints on memory, storage, and processing power; programming models, including synchronous, asynchronous, and concurrent; programming systems that run forever; use of Finite State Machines (FSMs); review of embedded hardware and available boards; asynchronous and synchronous serial connections (e.g., RS232 and SPI); General Purpose I/O (GPIO) pins; relays, servos, and other peripherals; design for low power; battery characteristics; systems that communicate over computer networks and the Internet; current industry practices; design of embedded projects; working in teams. CS 35200 (Compilers) and CS 35400 (Operating Systems) are recommended, but not required.', '202108', 'Regular', '', '3', 'PRProfessional', 'UPPRUpper Division', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '49000', 'Topics In CS For Ung', 'Topics In CS For Ung', 'Credit Hours: 1.00 to 5.00.  Supervised reading and reports in various fields. Permission of instructor required.', '202108', 'Regular', '', '0', 'GRGraduate', 'VARTVariable Title', '5');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '49200', 'Topics In CS', 'Topics In CS', 'Credit Hours: 3.00.  Seminar addressing current topics or issues in computer science or information systems.', '202108', 'Regular', '', '3', 'GRGraduate', 'VARTVariable Title', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '49400', 'Directed Study', 'Directed Study', 'Credit Hours: 1.00 to 3.00.  Independent study for students who desire to execute a complete computer- oriented project. Course may be repeated for credit up to nine hours toward graduation.', '202108', 'Regular', '', '1', 'GRGraduate', 'VARTVariable Title', '3');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '49500', 'Cooperative Experience', 'Cooperative Experience', 'Credit Hours: 0.00 to 3.00.  For cooperative program students only.', '202108', 'Regular', '', '0', 'GRGraduate', 'UPPRUpper Division', '3');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '49700', 'Honors Research Projct', 'Honors Research Projct', 'Credit Hours: 3.00.  One semester of the project may be counted as one of the seven computer science courses at or above the 300 level required for the bachelor\'s degree. One more semester, if approved by the honors coordinator, can be used as a free elective. A group research project directed by Computer Sciences faculty members. Each group must submit a technical report describing its work and the results obtained.', '202301', 'Regular', '', '3', 'GRGraduate', 'UPPRUpper Division', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '50010', 'Fndnl Principles Info Security', 'Fndnl Principles Info Security', 'Credit Hours: 3.00.  This course covers foundational principles relevant to information security including data structures, algorithm design, computational complexity, probability theory, number theory, and basics of cryptography. This course is restricted to CS graduate students. This course is required for students in the Master of Science in Computer Science Concentration in Information Security for Professionals Program. The course may not be used on the Plan of Study for any CS graduate students other than those in the Concentration in Information Security for Professionals program. Programming experience and computer science knowledge equivalent to that of a minor in CS. Incoming students are expected to have programming skills in at least one procedural programming languages, e.g., C,C++,Java, or Python.', '202301', 'Regular', '', '3', 'UGUndergraduate', 'UPPRUpper Division', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '50011', 'Intr Systems For Info Security', 'Intr Systems For Info Security', 'Credit Hours: 3.00.  The course covers computer systems principles relevant to information security and it serves as a prerequisite for the later courses in the MS in IS program. The material includes features in the C/C++ programming languages for understanding security-critical software vulnerabilities, basic knowledge in computer architecture and assembly languages, knowledge of the compiling process, operating systems, networking, databases, and web applications relevant to information security. This course is restricted to CS graduate students. This course is required for students in the Master of Science in Computer Science Concentration in Information Security for Professionals Program. The course may not be used on the Plan of Study for any CS graduate students other than those in the Concentration in Information Security for Processionals program. Programming experience and computer science knowledge equivalent to that of a minor in CS. Incoming students are expected to have programming skills in at least one procedural programming languages, e.g., C,C++,Java, or Python.', '202301', 'Regular', '', '0', 'PRProfessional', 'UPPRUpper Division', '3');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '50023', 'Data Engineering I', 'Data Engineering I', 'Credit Hours: 1.00.  The course introduces students to the fundamentals of Data Engineering with a focus on tools and computational techniques to gather, construct, manipulate, summarize, and visualize data sets as a means to extract knowledge from the underlying data. Python and Python libraries are used. Completion of the course will allow learners to perform basic data analysis on data sets. Experience in Python Programming and Linear Algebra is required. The course also prepares learners for additional instruction in the courses Data Engineering II and Foundations of Decision Making.', '202108', 'Regular', '', '1', 'PRProfessional', 'UPPRUpper Division', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '50024', 'Data Engineering II', 'Data Engineering II', 'Credit Hours: 1.00.  This course introduces students to the fundamentals of database management systems (DBMS) from a user\'s perspective. The principles of modeling an enterprise using Entity-Relationship diagrams and transforming the model into a relational or NoSQL database are illustrated through a range of examples.  The SQL language is used to create, query, aggregate, and update a relational database. NOSQL databases and the related data models (column, graph, and document-based) are introduced. Experience in Python Programming is required.', '202205', 'Regular', '', '1', 'PRProfessional', 'UPPRUpper Division', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '50025', 'Foundations Of Decision Making', 'Foundations Of Decision Making', 'Credit Hours: 1.00.  This course provides an overview of data science methods used for data-driven discovery, extraction of knowledge, and informed decision making. The course covers fundamental computational methods and statistical techniques used to correctly reason about uncertainty, conduct hypothesis tests, infer causal relationships, and apply and evaluate predictive models. The course highlights how sampling biases can impact fairness in decision making. Throughout, students get hands-on experience on how to make correct and explainable inferences from data. Experience in Python Programming, Probability, Statistics and Linear Algebra is required.', '202108', 'Regular', '', '1', 'PRProfessional', 'UPPRUpper Division', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '50100', 'Computing For Sci And Engrng', 'Computing For Sci And Engrng', 'Credit Hours: 3.00.  Credit in this course may not be used toward a graduate degree in Computer Sciences. Computational concepts, tools, and skills for computational science and engineering scripting for numerical computing, scripting for file processing, high performance computing, and software development. Project may be required.', '202108', 'Regular', '', '3', 'PRProfessional', 'UPPRUpper Division', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '50200', 'Compil & Progr Systms', 'Compil & Progr Systms', 'Credit Hours: 3.00.  Basic principles of compilers and compiler design; control of translation, loading, and execution; symbolic coding systems; lexical and syntactic analysis, design and operation of assemblers and macro processors; design of interpretive systems. Students are expected to complete a large programming project as part of the course.', '202108', 'Regular', '', '3', 'PRProfessional', 'UPPRUpper Division', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '50300', 'Operating Systems', 'Operating Systems', 'Credit Hours: 3.00.  Basic principles of operating systems: addressing modes, indexing, relative addressing, indirect addressing, stack maintenance; implementation of multitask systems; control and coordination of tasks, deadlocks, synchronization, mutual exclusion; storage management, segmentation, paging, virtual memory; protection, sharing, access control; file systems; resource management; evaluation and prediction of performance. Students are expected to spend at least three hours per week gaining hands-on experience in using and modifying a small operating system.', '202108', 'Regular', '', '0', 'PRProfessional', 'UPPRUpper Division', '3');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '50500', 'Distributed Systems', 'Distributed Systems', 'Credit Hours: 3.00.  Foundations for building reliable distributed systems, including failure and system models, and basic communication and agreement problems; crash failures, recovery, partition, Byzantine failures; asynchronous systems, failure detectors, communication channels, wireless and sensor networks; software clocks, causality, and cuts. Examples of problems include reliable broadcast consensus, leader election, group communication, and replication. Permission of department required.', '202108', 'Regular', '', '3', 'GRGraduate', 'UPPRUpper Division', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '51000', 'Software Engineering', 'Software Engineering', 'Credit Hours: 3.00.  Software life cycles, requirements engineering, software design, design of distributed systems, verification and validation, software architecture, process metrics and models, and research methods in software engineering.', '202108', 'Regular', '', '3', 'PRProfessional', 'UPPRUpper Division', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '51400', 'Numerical Analysis', 'Numerical Analysis', 'Credit Hours: 3.00.  (MA 51400)  AIternative methods for solving nonlinear equations; linear difference equations, applications to solution of polynomial equations; differentiation and integration formulas; numerical solution of ordinary differential equations; roundoff error bounds.', '202108', 'Regular', '', '3', 'PRProfessional', 'UPPRUpper Division', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '51500', 'Numerical Linear Algeb', 'Numerical Linear Algeb', 'Credit Hours: 3.00.  Direct and iterative solvers of dense and sparse linear systems of equations, numerical schemes for handling symmetric algebraic eigenvalue problems, and the singular-value decomposition and its applications in linear least squares problems.', '202108', 'Regular', '', '3', 'PRProfessional', 'UPPRUpper Division', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '51501', 'Parallelism Numer Linear Alg', 'Parallelism Numer Linear Alg', 'Credit Hours: 3.00.  This course examines both theoretical and practical aspects of numerical algorithm design and implementation on parallel computing platforms. In particular, it provides an understanding of the tradeoff between arithmetic complexity and management of hierarchical memory structures, roundoff characteristics if different from the sequential schemes, and performance evaluation and enhancement. Applications are derived from a variety of computational science and engineering areas.', '202301', 'Regular', '', '3', 'GRGraduate', 'UPPRUpper Division', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '51510', 'Algorithms', 'Algorithms', 'Credit Hours: 3.00.  This course concentrates on the design of algorithms and the rigorous analysis of their efficiency. We will cover various algorithm design techniques such as divide and conquer, dynamic programming, greedy algorithms, and approximation algorithms; for each algorithm, we will perform complexity (worst case, average case) analysis.', '202108', 'Regular', '', '3', 'PRProfessional', 'UPPRUpper Division', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '51520', 'Operating Systems', 'Operating Systems', 'Credit Hours: 3.00.  This course is about the concepts and principles of modern operation systems. It includes: design and implementation of multi-process systems; process synchronization, mutual exclusion; CPU scheduling, deadlock, memory management, segmentation, paging, virtual memory; storage management, file system management, protection and security, evaluation and prediction of performance. Reading the latest paper about operation systems and presentations are required. Every student should participate in debates based on the case studies of Linux System and Windows 7. Prerequisites: CS 30200. Permission of department required.', '202108', 'Regular', '', '3', 'PRProfessional', '', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '51530', 'Programming Languages', 'Programming Languages', 'Credit Hours: 3.00.  This course provides the student with an overview of the issues that arise in the design and construction of translators for programming languages. The course emphasizes techniques that have direct application to the construction of compilers. Students are expected to develop a fundamental understanding of the issues that arise in program translation, including syntax analysis, translation, and rudimentary program optimization. Prerequisites: Graduate student standing. Undergraduate course work on high-level programming languages, and data structure. Permission of department required.', '202108', 'Regular', '', '3', 'PRProfessional', '', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '51540', 'Object Oriented Design', 'Object Oriented Design', 'Credit Hours: 3.00.  This course is for students who already have programming experience. The course exposes students to the depth and breadth of modern programming practice, with the goal of making students better programmers. It provides a rigorous introduction to the advanced concepts behind object oriented programming such as encapsulation, information hiding, inheritance, dynamic binding and polymorphism. We discuss object-oriented design, design patterns and see how they can be implemented in different object-oriented programming languages. Java and C++ are used as the vehicle for illustrating and implementing these concepts. Permission of department required.', '202108', 'Regular', '', '3', 'PRProfessional', 'UPPRUpper Division', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '51550', 'Database Systems', 'Database Systems', 'Credit Hours: 3.00.  This course provides an introduction to modern database systems. It covers conceptual modeling and database design, formal database design theory, relational data model and SQL, query optimization, external hashing and indexing, and transaction processing, etc. In addition, this course provides fundamental theory, and methodologies of Data Mining. Students will be asked to apply the data mining knowledge for real world problem solving. Prerequisites: Undergraduate studies in CS, particularly CS 44200 or its equivalent. Permission of department required.
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '51560', 'Software Engineering', 'Software Engineering', 'Credit Hours: 3.00.  Software engineering is the discipline concerned with the application of theory, knowledge, and practice for effectively and efficiently building software systems that satisfy the requirements of users and customers. This course provides an introduction to all phases of the life cycle of a software system, including requirement analysis and specification, UML modeling and design, implementation, testing, and operation and maintenance. The principles of project management, cost and effort estimation, scheduling, documentation, and quality assurance are also covered. A group project will be assigned. Each student will play one of the following roles: Project Manager (PM), Requirement Engineer (RE), Software Architect (SA), Integration Engineer (IE), Testing Engineer (TE), and User/Product Director (UPD). Every student will play the role of a Code Developer (CD) as well. Every student should also participate in the activities of the professional associations. The commercial and research prototype tools of IBM Rational Suite will be used. Some latest research papers about Software Engineering will be discussed.', '202108', 'Regular', '', '3', 'PRProfessional', 'UPPRUpper Division', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '51570', 'Computer Architecture', 'Computer Architecture', 'Credit Hours: 3.00.  This course is the science and art of selecting and interconnecting hardware components to create computers that meet functional, performance and cost goals. It includes the following topics: Fundamentals of computer design, Instruction set principles and examples, Pipelining, Instruction-Level Parallelism and its dynamic exploitation, exploiting Instruction-Level Parallelism with software approaches, Memory hierarchy design, Parallel Processors and Cloud Computing. RISC, Intel 80x86, VAX, and IBM 360/370 are examples of the computer architecture for discussion. Reading and presenting the latest published papers related branch prediction and instruction-level parallelism are required. A project about designing branch prediction algorithms and evaluating the algorithms using SPEC benchmarks is also required.', '202305', 'Regular', '', '3', 'PRProfessional', 'UPPRUpper Division', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '51580', 'Computer Graphics', 'Computer Graphics', 'Credit Hours: 3.00.  An introduction to advanced computer graphics and the rendering of 3D computer graphics images. Topics include the concepts, principles, algorithms, and programming techniques in 3D interactive computer graphics. Emphasis is on the development and applications of 3D graphic algorithms and methods.', '202108', 'Regular', '', '3', 'PRProfessional', 'UPPRUpper Division', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '51590', 'Parallel Computing', 'Parallel Computing', 'Credit Hours: 3.00.  Parallel computing for science and engineering applications: parallel programming and performance evaluation, parallel libraries and problem-solving environments, models of parallel computing and run-time support systems, and selected applications.', '202108', 'Regular', '', '3', 'PRProfessional', 'UPPRUpper Division', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '52000', 'Comput Meth In Optimization', 'Comput Meth In Optimization', 'Credit Hours: 3.00.  A treatment of numerical algorithms and software for optimization problems with a secondary emphasis on linear and nonlinear systems of equations: unconstrained and constrained optimization; line search methods; trust region methods; Quasi-Newton methods; linear programming; calculating derivatives; quadratic programming; global optimization, including simulated annealing.', '202108', 'Regular', '', '3', 'PRProfessional', 'UPPRUpper Division', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '52200', 'High-Performance Computing', 'High-Performance Computing', 'Credit Hours: 3.00.  This course is an introductory course on high-performance computing. High-performance computing refers to the use of everything from software to hardware to speed up computations. As the CPU clock speed of desktop and commodity processors has decayed due to physical limitations, more creative use of software and parallel hardware is required to further speed up data processing. To program and accelerate applications on the new high-performance computing devices, we must understand both the computational architecture and the principles of program optimization. The driving outcome for this course is for students to understand and utilize high-performance computing concepts, architectures, and tools to develop and run optimized code for shared- and distributed-memory parallel programming models. Topics usually include parallel programming models, performance analysis, shared memory programming, parallel algorithm design, programming with message-passing, and advanced topics such as CUDA and hybrid programming.', '202308', 'Regular', '', '3', 'GRGraduate', 'UPPRUpper Division', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '52300', 'Social Econ Legal Asp Security', 'Social Econ Legal Asp Security', 'Credit Hours: 3.00.  This course focuses on social, legal, and economic aspects of information security and privacy, also including ethics, policies, and human behavioral issues. The course covers the interactions between non-technological aspects of information security as well as relevant technological aspects. It focuses on how non-technological facets can inform and guide technological choices, and how technological choices can enhance or detract from the broader organizational and societal goals.', '202301', 'Regular', '', '3', 'PRProfessional', 'UPPRUpper Division', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '52500', 'Parallel Computing', 'Parallel Computing', 'Credit Hours: 3.00.  Parallel computing for science and engineering applications: parallel programming and performance evaluation, parallel libraries and problem-solving environments, models of parallel computing and run-time support systems, and selected applications..', '202108', 'Regular', '', '3', 'PRProfessional', 'UPPRUpper Division', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '52510', 'Distributed Systems', 'Distributed Systems', 'Credit Hours: 3.00.  This course consists of the discussion of special problems related to distributed control such as election and mutual exclusion, routing, data management, Byzantine agreement, and deadlock handling. The student will get exposed to fundamental issues in distributed system design, recent development, and even research trends in this area, as well as the hands-on experience of using Java sockets to program and implement a distributed system. Prerequisites: Undergraduate coursework in programming languages, operation systems, computer architecture, and algorithm design. Permission of department required.', '202108', 'Regular', '', '3', 'PRProfessional', '', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '52520', 'Software Design I', 'Software Design I', 'Credit Hours: 3.00.  This is a first course in Software Design, which is an internship-training course that the PNW Computer Science faculty conducts in cooperation with a local software industry partner, with students working on software development projects for the partner.  Over the past few years the project has been run in conjunction with the Valparaiso, Indiana based software development company, BEULAHWORKS, LLC. This course introduces various topics related to software design including Object-Oriented Analysis & Design, Object-Oriented Design Principles, UML Diagrams, Architecture Design & Patters and other topics. In addition, students will participate in developing software for software industry use. In particular, students will work in small teams to design and implement new features for a commercial software project. By the end of the course, students should gain practical experience with enterprise software design and implementation. The first half of the semester will be devoted to lectures regarding software design (assignments related to the lectures will also be given during this time). For the second half, students will be working on the project. The students who get a B or better grade will be able to take the second course of the sequence, Software Design II. Permission of instructor required.', '202208', 'Regular', '', '0', 'PRProfessional', 'UPPRUpper Division', '3');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '52530', 'Software Design II', 'Software Design II', 'Credit Hours: 3.00.  This is a second course in Software Design, which is an internship-training course that the PNW Computer Science faculty conducts in cooperation with a local software industry partner. It is based on Software Design I, and allows students to continue to work on software development projects for the partner. Over the past few years the project has been run in conjunction with the Valparaiso, Indiana based software development company, BEULAHWORKS, LLC.  This course introduces various topics related to software design, including Object-Oriented Analysis & Design, Object-Oriented Design Principles, Design Patterns, Axiomatic Design and other topics.  In addition, students will participate in developing software for software industry use. In particular, students will work in small teams to design and implement new features for a commercial software system. By the end of the course, students should gain practical experience with enterprise software design and implementation. The first half of the semester will be devoted to lectures regarding software design (assignments related to the lectures will also be given during this time). For the second half, students will be working on the project. Permission of instructor required.
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '52540', 'Data Mining & Machine Learning', 'Data Mining & Machine Learning', 'Credit Hours: 3.00.  This course combines computer science algorithms and statistics analysis for data mining and machine learning. The course introduces rule-based decision tree systems, statistical learning, supervised and unsupervised learning, and multi-layer Neural Networks deep learning. Students will do modeling with C5, Neural Networks and R, and implement multiple algorithms in the programming languages C and Python.', '202108', 'Regular', '', '3', 'PRProfessional', 'UPPRUpper Division', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '52600', 'Information Security', 'Information Security', 'Credit Hours: 3.00.  (CSCI 52600) Basic notions of confidentiality, integrity, availability; authentication models; protection models; security kernels; secure programming; audit; intrusion detection and response; operational security issues; physical security issues; personnel security; policy formation and enforcement; access controls; information flow; legal and social issues; identification and authentication in local and distributed systems; classification and trust modeling; and risk assessment.', '202301', 'Regular', '', '3', 'PRProfessional', 'UPPRUpper Division', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '52700', 'Software Security', 'Software Security', 'Credit Hours: 3.00.  This course focuses on software security fundamentals, secure coding guidelines and principles, and advanced software security concepts. Students will learn to assess and understand threats, learn how to design and implement secure software systems, and get hands-on experience with common security pitfalls. Restricted to CS MS and CS PHD students. Significant programming experience and skills are required to complete the labs and projects.', '202108', 'Regular', '', '0', 'PRProfessional', 'UPPRUpper Division', '3');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '52800', 'Network Security', 'Network Security', 'Credit Hours: 3.00.  The course focuses on the principles and foundations of building secure network systems and on security and privacy challenges in existing and emerging networks. The course compares and analyzes network architectures and network protocols from the physical layer to the access control, network, transport and application layer from an adversarial standpoint to understand how to build more secure protocols that can withstand attacks. Restricted to CS MS and CS PHD students. Significant programming experience and skills are required to complete the projects.', '202108', 'Regular', '', '0', 'PRProfessional', 'UPPRUpper Division', '3');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '52900', 'Security Analytics', 'Security Analytics', 'Credit Hours: 3.00.  This course focuses on applied data mining, machine learning, data analytics techniques, and their application and relevance in information security. The course covers basic concepts of data mining and machine learning, computation platforms in support of big data analytics including Map-Reduce and Spark, machine learning algorithms such as classification trees, logistic regression, naive Bayes, k Nearest Neighbors, Support Vector Machines, Artificial Neural Networks (including Feed Forward, Convolutional, and Recurrence), the application of these algorithms to security tasks such as Spam/Phishing detection, malware detection, intrusion detection, and situational awareness. The future and potential role of applying machine learning techniques in information and data security is explored.', '202208', 'Regular', '', '3', 'PRProfessional', 'UPPRUpper Division', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '53000', 'Intro Scient Visualiz', 'Intro Scient Visualiz', 'Credit Hours: 3.00.  Teaches the fundamentals of scientific visualization and prepares students to apply these techniques in fields such as astronomy, biology, chemistry, engineering, and physics. Emphasis is on the representation of scalar, vector, and tensor fields; data sampling and resampling; and reconstruction using multivariate finite elements (surfaces, volumes, and surfaces on surfaces).', '202108', 'Regular', '', '3', 'PRProfessional', 'UPPRUpper Division', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '53100', 'Computational Geometry', 'Computational Geometry', 'Credit Hours: 3.00.  Computational geometry studies how to compute with and reason about geometric objects. The subject is playing an increasingly important role in computer graphics, game software, geometric modeling, geographic information systems, and many other applications. Course topics include convex hull, segment manipulations, triangulations, range searching, Voronoi diagrams, window queries, Delaunay triangulation, and duality. Some key algorithms are implemented. Questions of floating-point accuracy and robust algorithm design are considered throughout the course.', '202108', 'Regular', '', '3', 'PRProfessional', 'UPPRUpper Division', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '53500', 'Interact Comptr Graph', 'Interact Comptr Graph', 'Credit Hours: 3.00.  (ME 57300) The principles of computer graphics and interactive graphical methods for problem solving. Emphasis placed on both development and use of graphical tools for various display devices. Several classes of graphics hardware considered in detail. Topics include pen plotting, storage tubes, refresh, dynamic techniques, three dimensions, color, modeling of geometry, and hidden surface removal. Part of the laboratory involves use of an interactive minicomputer graphics system. Knowledge of programming required.', '202108', 'Regular', '', '3', 'PRProfessional', 'UPPRUpper Division', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '53600', 'Data Comm & Comp Netwk', 'Data Comm & Comp Netwk', 'Credit Hours: 3.00.  Data communications: communication hardware technologies including local area and longhaul network hardware, circuit and packet switching, interfaces between computer and network hardware, and performance issues. Network architecture: protocol software and conceptual layering, reliable delivery over an unreliable channel, transport protocols, virtual circuits, datagrams, internetworking as a fundamental design concept, the client-server paradigm, naming and name binding, name servers, addressing and address resolution, routing and routing algorithms, congestion and flow control techniques, network file systems, distribution of computation, and DARPA internet protocols (TCP/IP) as examples of protocol organization.', '202108', 'Regular', '', '0', 'PRProfessional', 'UPPRUpper Division', '3');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '54100', 'Database Systems', 'Database Systems', 'Credit Hours: 3.00.  Fundamentals for the logical design of database systems. The entity-relationship model, semantic model, relational model, hierarchical model, network model. Implementations of the models. Design theory for relational databases. Design of query languages and the use of semantics for query optimization. Design and verification of integrity assertions, and security. Introduction to intelligent query processing and database machines.', '202108', 'Regular', '', '3', 'PRProfessional', 'UPPRUpper Division', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '54200', 'Distrib Database Systs', 'Distrib Database Systs', 'Credit Hours: 3.00.  Fundamental issues in distributed database systems that are motivated by the computer networking and distribution of processors and databases. The theory, design, specification, implementation, and performance of distributed database systems.', '202108', 'Regular', '', '3', 'PRProfessional', 'UPPRUpper Division', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '54300', 'Simul & Model Comp Sys', 'Simul & Model Comp Sys', 'Credit Hours: 3.00.  Simulation: discrete event simulation, process oriented simulation, generating random numbers, simulation languages, simulation examples of complex systems. Nondeterministic models: random variables, Poisson process, moment generating functions, statistical inference and data analysis. Modeling: elementary queuing models, networks of queues, applications to performance evaluation of computer systems.', '202108', 'Regular', '', '3', 'PRProfessional', 'UPPRUpper Division', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '54701', 'Information Retrieval', 'Information Retrieval', 'Credit Hours: 3.00.  Basic principles and practical algorithms used for information retrieval and text mining: statistical characteristics of text, several important retrieval models, text categorization, recommendation system, clustering, federated text search, link analysis, etc.', '202108', 'Regular', '', '3', 'GRGraduate', 'UPPRUpper Division', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '55100', 'Cloud Computing Fundamentals', 'Cloud Computing Fundamentals', 'Credit Hours: 3.00.  A comprehensive course that investigates all aspects of cloud computing, including: cloud data centers and infrastructure (equipment for processing, storage, communication, and special-purpose facilities); the use of virtualized servers (virtual machines and containers), virtualized storage (Storage Area Networks, Network Attached Storage, object storage), and virtual networks; automation; orchestration systems, such as Kubernetes; programming paradigms used to construct cloud-native software, such as MapReduce, microservices, controllerbased computing, and serverless computing; edge computing; security and privacy in cloud systems; software models. The course emphasizes researching new ways to use cloud computing and research into new ways to design and build cloud systems. The course includes a project. Students should have completed coursework in or have equivalent familiarity with operating systems and computer networks.', '202301', 'Regular', '', '3', 'PRProfessional', 'UPPRUpper Division', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '55500', 'Cryptography', 'Cryptography', 'Credit Hours: 3.00.  (CSCI 55500) Concepts and principles of cryptography and data security. Cryptography (secret codes): principles of secrecy systems; classical cryptographic systems, including Vigenere and Vernam ciphers; the Data Encryption Standard (DES); public-key encryption; privacy-enhanced email; digital signatures. Proprietary software protection; information theory and number theory; complexity bounds on encryption; key escrow; traffic analysis; attacks against encryption; basic legal issues; e-commerce; and the role of protocols.', '202301', 'Regular', '', '3', 'PRProfessional', 'UPPRUpper Division', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '55600', 'Data Security And Privacy', 'Data Security And Privacy', 'Credit Hours: 3.00.  Data security and privacy is an important part of information security. This course provides an introduction to the fundamental principles and techniques for data security and privacy. The course covers fundamental theories of access control, discretionary access control, mandatory access control, and role-based access control, database access control approaches including grant revoke, virtual private databases and labelled databases, database encryption. On privacy, it covers data anonymization, re-identification attacks, definition, primitives, and applications of differential privacy.', '202301', 'Regular', '', '3', 'PRProfessional', 'UPPRUpper Division', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '56000', 'Reasoning About Programs', 'Reasoning About Programs', 'Credit Hours: 3.00.  The course focuses on the logical foundations and algorithmic techniques used to ensure program correctness. With an emphasis on automated program verification and synthesis, the course covers classical concepts and techniques such as Hoare logic, abstract interpretation, abstraction-refinement and bounded model checking. The course also exposes students to approaches for program synthesis, a new frontier for program reasoning, such as inductive synthesis, synthesis using version space algebras, constraint-based synthesis and synthesis based on machine-learning techniques. The courses emphasizes both theoretical foundations as well as hands-on experience with verification/synthesis tools and SAT/SMT solvers. Students are expected to have completed undergraduate coursework in Foundations of Computer Science (CS 18200) or equivalent, Systems Programming (CS 25200) or equivalent, and Introduction to the Analysis of Algorithms (CS 38100) or equivalent.  Mathematical maturity is expected.', '202108', 'Regular', '', '3', 'PRProfessional', 'UPPRUpper Division', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '56500', 'Programming Languages', 'Programming Languages', 'Credit Hours: 3.00.  An exploration of modern or unconventional concepts of programming languages, their semantics, and their implementations; abstract data types; axiomatic semantics using Hoare\'s logic and Dijkstra\'s predicate transformers; denotational semantics; functional, object-oriented, and logic programming; concurrency and Owicki-Gries theory. Example languages include ML, Ada, Oberon, LISP, PROLOG, and CSP.', '202108', 'Regular', '', '3', 'PRProfessional', 'UPPRUpper Division', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '57100', 'Artificial Intelligence', 'Artificial Intelligence', 'Credit Hours: 3.00.  Artificial Intelligence (AI) systems are increasingly being deployed in many real-world tasks. This course provides an introduction to the fundamental principles and applications of AI. The course covers classic material including search-based methods, probabilistic reasoning, game playing, decision making, exact and approximate inference, causal learning, and reinforcement learning as well as selected advanced topics. The focus of the course is on foundational methods and current techniques for building AI systems that exhibit \'intelligent\' behavior and can \'learn\' from experience. The course assumes students are familiar with basic concepts in analysis, linear algebra, optimization, discrete mathematics, elementary probability, statistics, data structures, and algorithms. Students are expected to have good programming and software development skills and have a working knowledge of Python and Java.', '202205', 'Regular', '', '3', 'PRProfessional', 'UPPRUpper Division', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '57200', 'Heuristic Prblm Solvng', 'Heuristic Prblm Solvng', 'Credit Hours: 3.00.  Design and development of heuristic problem-solving systems. The emphasis is on the development of general data representations, heuristics, and problem-solving strategies that can be applied to a wide class of problems. The task areas explored include game playing, theorem proving, pattern recognition, semantic information processing, cognitive psychology, design synthesis, robotology, and integrated artificial intelligence systems.', '202108', 'Regular', '', '3', 'PRProfessional', 'UPPRUpper Division', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '57300', 'Data Mining', 'Data Mining', 'Credit Hours: 3.00.  (CSCI 57300) Data Mining has emerged at the confluence of artificial intelligence, statistics, and databases as a technique for automatically discovering summary knowledge in large datasets. This course introduces students to the process and main techniques in data mining, including classification, clustering, and pattern mining approaches. Data mining systems and applications are also covered, along with selected topics in current research. Offered in alternate years.', '202108', 'Regular', '', '3', 'PRProfessional', '', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '57600', 'Machine Learning', 'Machine Learning', 'Credit Hours: 3.00.  Machine Learning is concerned with computer programs that "automatically" improve their performance through experience (based on data). As an introductory course to machine learning, the course introduces the fundamentals of modern machine learning. It will give a broad overview of many concepts and algorithms in machine learning, ranging from supervised learning to unsupervised learning. Topics include decision tree learning, instance-based learning, perceptron and linear modeling, probabilistic modeling, neural networks, support vector machines, ensemble learning, learning theory, and unsupervised learning with clustering. This course will provide a combination of theoretical knowledge and practical, hands-on experience in solving real-world problems through the applications of machine learning. Basic computer science concepts, data structure, algorithm, programming experiences, knowledge of linear algebra, basic statistics, and probability is required.', '202108', 'Regular', '', '3', 'PRProfessional', 'UPPRUpper Division', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '57700', 'Natural Language Processing', 'Natural Language Processing', 'Credit Hours: 3.00.  This course will cover the key concepts and methods used in modern Natural Language Processing (NLP). Throughout the course several core NLP tasks, such as sentiment analysis, information extraction, syntactic and semantic analysis, will be discussed. The course will emphasize machine-learning and data-driven algorithms and techniques, and will compare several different approaches to these problems in terms of their performance, supervision effort and computational complexity. Prerequisites: A background in linear algebra, calculus, statistics and probability, and completion of CS 57800 or equivalent are highly recommended. Strong programming skills in any modem language (Python, Java, C++) are required.', '202108', 'Regular', '', '3', 'PRProfessional', 'UPPRUpper Division', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '57800', 'Statistical Machine Learning', 'Statistical Machine Learning', 'Credit Hours: 3.00.  This introductory course will cover many concepts, models, and algorithms in machine learning. Topics include classical supervised learning (e.g., regression and classification), unsupervised learning (e.g., principle component analysis and K-means), and recent development in the machine learning field such as variational Bayes, expectation propagation, and Gaussian processes. While this course will give students the basic ideas and intuition behind modern machine learning methods, the underlying theme in the course is probabilistic inference.', '202108', 'Regular', '', '3', 'GRGraduate', 'UPPRUpper Division', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '57900', 'Bioinformatics Algorithms', 'Bioinformatics Algorithms', 'Credit Hours: 3.00.  Review of Genomes, DNA, RNA, proteins, proteomes. Biological Sequences: dynamic programming; pairwise global, local, and semi-global alignments of genes and proteins; constant, affine, and general gap penalties; RNA alignments; BLOSUM and PAM scoring matrices. Multiple alignment of proteins: approximation algorithms; iterative and progressive alignment methods. Database search for sequences: BLAST and variants. Phylogenetic Trees: distance-based methods, ultrametric and additive distance functions; parsimony, and maximum likelihood methods. Whole Genome Alignment: suffix trees and suffix arrays. Systems Biology: Module discovery in biological networks, spectral algorithms for graph clustering. Network alignment: quadratic programming formulations and graph matching. Genetic Variation: haplotype inference, the perfect phylogeny problem and chordal graphs. Additional topics such as next-generation sequencing, analysis of multidimensional data from flow cytometry, and gene expression data, if time permits.', '202108', 'Regular', '', '3', 'GRGraduate', 'UPPRUpper Division', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '58000', 'Alg Design Anly & Impl', 'Alg Design Anly & Impl', 'Credit Hours: 3.00.  Basic techniques for designing and analyzing algorithms: dynamic programming, divide and conquer, balancing. Upper and lower bounds on time and space costs, worst case and expected cost measures. A selection of applications such as disjoint set union/find, graph algorithms, search trees, pattern matching. The polynomial complexity classes P, NP, and co-NP; intractable problems.', '202108', 'Regular', '', '3', 'PRProfessional', 'UPPRUpper Division', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '58300', 'Big Data Analytics Cloud Compt', 'Big Data Analytics Cloud Compt', 'Credit Hours: 3.00.  This course brings together the principles of large-scale distributed computing and the state of the art for Big Data Analytics in the Cloud. This course will first overview Cloud computing which is a service model for large-scale distributed computing, and the representative platform, Hadoop. Then, the course will introduce MapReduce which is a scalable programming model for processing Big Data on a cluster and have in-depth analysis of various algorithmic design patterns, and go on to explore Big Data Analytics applications on Hadoop MapReduce and Spark. Afterwards, the course will introduce NoSQL systems for Big Data storage. This course will also show how to deal with services from public cloud infrastructures.', '202108', 'Regular', '', '3', 'GRGraduate', 'UPPRUpper Division', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '58400', 'Thry Comp/Comp Complex', 'Thry Comp/Comp Complex', 'Credit Hours: 3.00.  The theory of general purpose programming systems. Recursive and partial-recursive functions; recursive and recursively enumerable sets. The Church-Turing thesis. The recursion theorem, Rogers\' translation theorem, Rice\'s undecidability theorem. The general theory of computational complexity: there are no general solutions to natural optimization problems. Complexity for specific models of computation: the polynomial complexity classes P, NP, and PSPACE; NP-hard and PSPACE-hard problems, inherently exponential problems.', '202108', 'Regular', '', '3', 'PRProfessional', 'UPPRUpper Division', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '58500', 'TCS Toolkit', 'TCS Toolkit', 'Credit Hours: 3.00.  This course covers fundamental techniques and a range of mathematical tools that underlie today\'s research in theoretical computer science. The course material is essential for research in theoretical computer science as well as machine learning theory. The course is targeted at students who plan to pursue research in these areas. Topics will be chosen from four core areas: Convex Analysis and Optimization, Spectral Methods, Concentration Inequalities, and Discrete Fourier Analysis. Depending on student and instructor interest, additional topics will be chosen and may include applied analysis, coding theory, probabilistic proofs, and more advanced topics in discrete Fourier analysis. Students will read papers in theoretical computer science and machine learning theory using, exploring and extending the covered techniques and tools. Students are expected to be proficient in probability theory, have the maturity to follow and carry out basic analysis proofs, and have completed courses in calculus, linear algebra, discrete mathematics, and analysis of algorithms. More specifically, the course expects mastery of the material covered in Calculus III, Linear Algebra, Probability, Foundations of CS, and Analysis of Algorithms.', '202301', 'Regular', '', '3', 'PRProfessional', 'UPPRUpper Division', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '58800', 'Randomized Algorithms', 'Randomized Algorithms', 'Credit Hours: 3.00.  The area of randomized algorithms has grown from being a tool in computational number theory to finding widespread application in algorithms in all areas of computer science. Two practical benefits of randomization are its simplicity and speed. Deep and important computational complexity connections have been developed,  from bridging the gap between discrete and continuous optimization, bypassing deterministic barriers in online settings, summarizing massive data in polylogarithmic space, probabilistically checkable proofs, privacy enhancing techniques, modeling the web and other large networks, and sparsifying and compressing complicated combinatorial structures such as graphs. Randomization is an essential  key ingredient in the algorithmic breakthroughs today and in the future. This course presents the basic concepts in the design and analysis of randomized algorithms and shows how to apply them to algorithms for a wide range of problems. The course covers a broad range of problems benefitting from randomized computation including data structures, graph algorithms, online algorithms, geometric algorithms, streaming algorithms, derandomization techniques, and tools for probabilistic analysis of algorithms.', '202108', 'Regular', '', '3', 'PRProfessional', 'UPPRUpper Division', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '59000', 'Topic Computer Sci', 'Topic Computer Sci', 'Credit Hours: 1.00 to 5.00.  Directed study for students who wish to undertake individual reading and study on approved topics. Permission of instructor required.', '202108', 'Regular', '', '0', 'PRProfessional', 'VARTVariable Title', '5');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '59100', 'Seminar', 'Seminar', 'Credit Hours: 1.00.  A weekly seminar presented by faculty and invited speakers, normally in a specific area.', '202108', 'Regular', '', '1', 'PRProfessional', 'VARTVariable Title', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '59200', 'Computer Science Seminar', 'Computer Science Seminar', 'Credit Hours: 3.00.  A seminar course for in-depth exploration of a specific topic. Topics vary. Classes consist of both instructor and student presentations. Students read, present and discuss research papers, and may complete and present a research project in the area. For each offering, inclusion on a plan of study is determined by the CS Graduate Study Committee. If approved, may be included on a CS MS or PhD plan of study according to relevant ruleset.', '202108', 'Regular', '', '3', 'GRGraduate', 'VARTVariable Title', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '59300', 'New Topics In CS', 'New Topics In CS', 'Credit Hours: 3.00.  A variable title course for topics not currently covered in the CS graduate curriculum. Each offering follows a traditional course structure with textbook(s), assignments, exams, and week-by-week content synopsis described in a syllabus. A CS 59300 offering does not imply that a course is intended to become, or will become, a regular course in the CS graduate curriculum. May be included on a CS MS or PhD plan of study according to the relevant ruleset.', '202108', 'Regular', '', '3', 'GRGraduate', 'VARTVariable Title', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '59799', 'Graduate Professional Practice', 'Graduate Professional Practice', 'Credit Hours: 0.00.  Internship experience to complement the student\'s academic coursework and help prepare the student for employment in computer science. The student must present a letter from the proposed employer describing to a reasonable extent the work to be undertaken and find a member of the Computer Science Graduate Faculty to be the instructor. The student must have completed at least one semester as a computer science graduate student at Purdue, West Lafayette. May not be taken in successive semesters. Permission of instructor required.', '202108', 'Regular', '', '0', 'PRProfessional', 'INTRInternship', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '61500', 'Num Methods For PDEs I', 'Num Methods For PDEs I', 'Credit Hours: 3.00.  (MA 61500) Finite element method for elliptic partial differential equations; weak formulation; finite-dimensional approximations; error bounds; algorithmic issues; solving sparse linear systems; finite element method for parabolic partial differential equations; backward difference and Crank-Nicolson time-stepping; introduction to finite difference methods for elliptic, parabolic, and hyperbolic equations; stability, consistency, and convergence; discrete maximum principles. Prerequisite: CS 51400, MA 52300.', '202108', 'Regular', '', '3', 'UGUndergraduate', '', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '62600', 'Adv Info Assurance', 'Adv Info Assurance', 'Credit Hours: 3.00.  Advanced topics in information assurance, including selections from the following: penetration testing, formal verification of systems, formal models of information flow and protection, distributed system authentication, protocol design and attack, computer viruses and malware, intrusion and anomaly detection models, multi-level security, active defenses, investigation and forensics, network firewalls, anonymity and identity, e-commerce support, and database security models and mechanisms. Offered every third semester. Prerequisite: CS 52600, 55500.', '202108', 'Regular', '', '3', 'UGUndergraduate', '', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '63500', 'Capt & Renderng Scenes', 'Capt & Renderng Scenes', 'Credit Hours: 3.00.  Advanced techniques for automated modeling and visualization of complex environments, such as rooms, buildings, urban scenes, and landscapes. Topics covered are at the confluence of computer graphics, computer vision and engineering, and include:  capture technologies, acquisition device calibration, view registration, panoramic images, 3D reconstruction from images, view morphing, 3D image warping, light field modeling and rendering, visual hulls, point-based modeling and rendering, view dependent texture mapping, and application development issues and examples. Prerequisite: CS 58000, CS 53500 or a solid foundation in computer graphics, programming proficiency in C or C++ and basic linear algebra.', '202108', 'Regular', '', '3', 'UGUndergraduate', '', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '63600', 'Internetworking', 'Internetworking', 'Credit Hours: 3.00.  Principles of Internetwork architecture and communication protocols underlying interoperable systems. Topics include universal interconnection, Internet addressing and routing, address binding, control of Internet congestion and flow, examples of Internet protocol suites (TCP/IP and XNS), round trip time estimation, naming and name resolution, Internet applications programs, and the ISO/OSI model. Prerequisite: CS 50300.', '202108', 'Regular', '', '0', 'UGUndergraduate', '', '3');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '63800', 'Multim Ntwrk&Oper Syst', 'Multim Ntwrk&Oper Syst', 'Credit Hours: 3.00.  For graduate students in computer sciences who have a background in networking and operating systems and are interested in research in networked multimedia systems. The course consists of a selection of networking and operating system topics for realizing high performance, predictable, scalable, flexible, and secure multimedia systems. Topics may vary to reflect timely research issues and the current interests of the instructor(s). Students are expected to complete a term project. Presentations also may be required. Undergraduate background in computer science required.  Prerequisite: CS 53600 or 50300.', '202108', 'Regular', '', '3', 'UGUndergraduate', '', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '64100', 'Multim Database Systms', 'Multim Database Systms', 'Credit Hours: 3.00.  Prepares students for research in multimedia database systems. Students are exposed to a variety of emerging innovative techniques to store, manipulate, communicate, visualize, and reason with multimedia systems. Prerequisite: CS 54100 or 54200.', '202108', 'Regular', '', '3', 'UGUndergraduate', '', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '65500', 'Advanced Cryptology', 'Advanced Cryptology', 'Credit Hours: 3.00.  Advanced topics in cryptography and cryptanalysis, including selections from the following: Steganographic methods, Cryptanalytic techniques, including differential cryptanalysis, and chosen plaintext attacks. Message digest algorithm construction. Digital cash. Quantum cryptography. N-key systems. Minimal and zero-knowledge systems. Protocol design and failure. Verification of algorithms. Key generation and management. Traffic analysis. VPN construction and operation. Politics, espionage, and law enforcement concerns. Offered every third semester. Prerequisite: CS 52600, 55500.', '202108', 'Regular', '', '3', 'UGUndergraduate', '', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '66200', 'Pattn Recog/Decis Proc', 'Pattn Recog/Decis Proc', 'Credit Hours: 3.00.  (ECE 66200)  Introduction to the basic concepts and various approaches of pattern recognition and decision-making processes. Topics include various classifier designs, evaluation of classifiability, learning machines, feature extraction, and modeling. Prerequisite: ECE 30200', '202108', 'Regular', '', '3', 'UGUndergraduate', '', '');
