INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', '69800', 'Research MS Thesis', 'Research MS Thesis', 'Credit Hours: 1.00 to 18.00.  Research MS Thesis. The student is expected to follow safe research practices, to follow ethical research practices, to maintain communication with the instructor, to formulate a set of reasonable semester research progress milestones with instructor, optionally in writing. Permission of instructor required.', '202108', 'Regular', '', '1', 'UGUndergraduate', '', '18');
