INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', 'B1040', 'Micro Lit', 'Micro Lit', 'Credit Hours: 3.00.  Introduction to the microcomputer as a word processing, spreadsheet, database, and communications tool. Intended for those in other disciplines. Hardware and software trends, opportunities, and responsibilities in computing are discussed. Lecture and laboratory. Not open to students who have credit in CS 102.', '202108', 'Regular', '', '0', 'GRGraduate', 'LOWRLower Division', '3');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', 'B1160', 'Visual Programming', 'Visual Programming', 'Credit Hours: 3.00.  Introduction for non-computer science majors in structured computer programming using a language such as Visual Basic. Modular programming techniques with emphasis on the creation of graphical user interfaces.', '202108', 'Regular', '', '3', 'GRGraduate', 'LOWRLower Division', '');
INSERT INTO `Courses` (`Organization`, `CourseSubjectAbbreviation`, `CourseNumber`, `CourseShortTitle`, `CourseLongTitle`, `CourseDescription`, `CourseEffectiveDate`, `CourseCreditBasis`, `CourseCreditUnits`, `CourseCreditMinimumValue`, `CourseLevel`, `CourseAttribute`, `CourseCreditMaximumValue`) VALUES ('', 'CS', 'B1200', 'Comp Science I', 'Comp Science I', 'Credit Hours: 3.00.  Introduction to programming using a high-level language and an established programming paradigm. Emphasis on developing problem-solving skills and programming techniques. Topics include control structures, built-in data structures, simple sorting and searching, procedural abstraction, and paradigm-specific concepts and constructs. Required open-lab and closed-lab assignments bring theory to practice.', '202108', 'Regular', '', '0', 'GRGraduate', 'LOWRLower Division', '3');
