import React, { useState } from "react";
import Field from "../forms/Field";

async function getSessions(courseName) {
    try {
        var url = new URL(process.env.REACT_APP_API_URL + '/Report/Session');
        url.searchParams.append('courseName', courseName);

        var myHeaders = new Headers();
        myHeaders.append("Ocp-Apim-Subscription-Key", process.env.REACT_APP_SUBSCRIPTION_KEY);

        var requestOptions = {
            method: 'GET',
            headers: myHeaders,
        };
        const response = await fetch(url, requestOptions);
        if (!response.ok) {
            throw new Error(`Failed to fetch data. Status: ${response.status}`);
        }
        const data = await response.json();
        return data;
    } catch (error) {
        console.error('Error:', error);
    }
}

function Filter({onFormSubmit}) {
    const [formState, setFormState] = useState({
        courseName: ''
    });

    const handleInputChange = (e) => {
        // handle input changes and update formState
        setFormState({
            ...formState,
            [e.target.name]: e.target.value,
        });
    };
    const handleSubmit = async (e) => {
        e.preventDefault();

        try {
            const data = await getSessions(courseName);
            onFormSubmit(data);
            // Handle the fetched data as needed
        } catch (error) {
            // Handle errors
            console.error("Error fetching data:", error);
        }
    };

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormState({
            ...formState,
            [name]: value,
        });
    };

    return (
        <>
            <h2>Filter Courses</h2>
            <form onSubmit={handleSubmit}>
                <Field name={"courseName"} labelName={"Course Name"} handleChange={handleChange} />
                <button className="bg-[var(--md-sys-color-primary-container-dark)] hover:bg-[var(--md-sys-color-surface-dark)] text-[var(--md-sys-color-on-primary-container-dark)] font-bold py-2 px-4 rounded-full" type="submit">
                    Submit
                </button>
            </form>
        </>
    );
}

export default Filter;
